{
  "name": "PadelPro TMS",
  "description": "A comprehensive Padel Tennis tournament management system featuring real-time scoring, bracket management, and referee tools."
}

export enum TournamentFormat {
  SINGLE_ELIMINATION = 'SINGLE_ELIMINATION',
  DOUBLE_ELIMINATION = 'DOUBLE_ELIMINATION',
  ROUND_ROBIN = 'ROUND_ROBIN'
}

export enum SkillLevel {
  NEWCOMER = 'NEWCOMER',
  BEGINNER = 'BEGINNER',
  INTERMEDIATE = 'INTERMEDIATE',
  ADVANCED = 'ADVANCED',
  PROFESSIONAL = 'PROFESSIONAL'
}

export enum MatchStatus {
  SCHEDULED = 'SCHEDULED',
  IN_PROGRESS = 'IN_PROGRESS',
  COMPLETED = 'COMPLETED'
}

export enum RegistrationStatus {
  PENDING = 'PENDING',
  ACCEPTED = 'ACCEPTED',
  REJECTED = 'REJECTED'
}

export interface Player {
  name: string;
  phone: string;
  email: string;
  photoUrl?: string; // Base64 for MVP
  verified: boolean;
}

export interface Team {
  id: string;
  name: string;
  player1: Player;
  player2: Player;
  status: RegistrationStatus;
  registeredAt: string;
  wins?: number; // For Round Robin
  losses?: number; // For Round Robin
  points?: number; // For Round Robin
}

export interface ScoreState {
  p1Points: string; // 0, 15, 30, 40, AD
  p2Points: string;
  p1Games: number;
  p2Games: number;
  p1Sets: number;
  p2Sets: number;
  currentSet: number; // 1, 2, 3
  isTiebreak: boolean;
  history: string[]; // Log of points
}

export interface Match {
  id: string;
  tournamentId: string;
  team1Id?: string; // Can be undefined in bracket initially
  team2Id?: string;
  round: number; // 1 = Final, 2 = Semi, 4 = Quarter, etc. (Inverse for easy logic) or 1,2,3 for sequential
  roundName: string; // "Quarter Finals", "Finals"
  court: string;
  scheduledTime: string;
  status: MatchStatus;
  score: ScoreState;
  winnerTeamId?: string;
  refereeNotes?: string[]; // Skill tags
}

export interface Tournament {
  id: string;
  name: string;
  format: TournamentFormat;
  skillLevel: SkillLevel;
  maxTeams: number;
  entryFee: number;
  prizeMoney: number;
  refereePasscode: string;
  courts: string[]; // e.g., ["Court 1", "Court 2"]
  registrationDeadline: string;
  teams: Team[];
  matches: Match[];
  status: 'DRAFT' | 'ACTIVE' | 'COMPLETED';
}

export interface GlobalState {
  tournaments: Tournament[];
  currentTournamentId: string | null;
}

import { GlobalState, Tournament, Team, Match, MatchStatus, ScoreState, TournamentFormat } from '../types';

const STORAGE_KEY = 'padel_pro_db_v1';

const initialScore: ScoreState = {
  p1Points: '0',
  p2Points: '0',
  p1Games: 0,
  p2Games: 0,
  p1Sets: 0,
  p2Sets: 0,
  currentSet: 1,
  isTiebreak: false,
  history: []
};

const getDB = (): GlobalState => {
  const data = localStorage.getItem(STORAGE_KEY);
  if (data) return JSON.parse(data);
  return { tournaments: [], currentTournamentId: null };
};

const saveDB = (db: GlobalState) => {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(db));
};

export const createTournament = (t: Omit<Tournament, 'id' | 'teams' | 'matches' | 'status'>): Tournament => {
  const db = getDB();
  const newTournament: Tournament = {
    ...t,
    id: Date.now().toString(),
    teams: [],
    matches: [],
    status: 'ACTIVE' // Simplified for MVP
  };
  db.tournaments.push(newTournament);
  db.currentTournamentId = newTournament.id;
  saveDB(db);
  return newTournament;
};

export const getCurrentTournament = (): Tournament | null => {
  const db = getDB();
  if (!db.currentTournamentId) return null;
  return db.tournaments.find(t => t.id === db.currentTournamentId) || null;
};

export const registerTeam = (tournamentId: string, team: Omit<Team, 'id' | 'status' | 'registeredAt'>) => {
  const db = getDB();
  const tIndex = db.tournaments.findIndex(t => t.id === tournamentId);
  if (tIndex === -1) throw new Error("Tournament not found");

  const newTeam: Team = {
    ...team,
    id: Math.random().toString(36).substr(2, 9),
    status: 'PENDING',
    registeredAt: new Date().toISOString()
  };

  db.tournaments[tIndex].teams.push(newTeam);
  saveDB(db);
};

export const updateTeamStatus = (tournamentId: string, teamId: string, status: 'ACCEPTED' | 'REJECTED') => {
  const db = getDB();
  const t = db.tournaments.find(t => t.id === tournamentId);
  if (!t) return;
  const team = t.teams.find(tm => tm.id === teamId);
  if (team) {
    team.status = status;
    saveDB(db);
  }
};

export const generateSchedule = (tournamentId: string) => {
  const db = getDB();
  const t = db.tournaments.find(t => t.id === tournamentId);
  if (!t) return;

  const acceptedTeams = t.teams.filter(tm => tm.status === 'ACCEPTED');

  // Simple Single Elimination Generator for MVP
  // If we have < 4 teams, just a final. 4-8, quarters etc.
  // This is a naive implementation for demonstration.

  const matches: Match[] = [];

  // Create first round matches
  for (let i = 0; i < acceptedTeams.length; i += 2) {
    if (i + 1 < acceptedTeams.length) {
      matches.push({
        id: Math.random().toString(36).substr(2, 9),
        tournamentId,
        team1Id: acceptedTeams[i].id,
        team2Id: acceptedTeams[i + 1].id,
        round: 1, // Round 1
        roundName: acceptedTeams.length > 4 ? "Quarter Finals" : "Semi Finals",
        court: t.courts[i % t.courts.length] || "Court 1",
        scheduledTime: new Date(new Date().getTime() + (i * 60 * 60 * 1000)).toISOString(), // Staggered by hours
        status: MatchStatus.SCHEDULED,
        score: JSON.parse(JSON.stringify(initialScore))
      });
    }
  }

  // Add a placeholder Final
  matches.push({
      id: "final_match",
      tournamentId,
      team1Id: undefined,
      team2Id: undefined,
      round: 2,
      roundName: "Grand Final",
      court: "Court 1",
      scheduledTime: new Date(new Date().getTime() + (24 * 60 * 60 * 1000)).toISOString(),
      status: MatchStatus.SCHEDULED,
      score: JSON.parse(JSON.stringify(initialScore))
  });

  t.matches = matches;
  saveDB(db);
};

export const updateMatchScore = (matchId: string, newScore: ScoreState, status: MatchStatus, winnerId?: string) => {
  const db = getDB();
  // Search in all tournaments for the match (inefficient but safe for mock)
  for (const t of db.tournaments) {
    const mIndex = t.matches.findIndex(m => m.id === matchId);
    if (mIndex !== -1) {
      t.matches[mIndex].score = newScore;
      t.matches[mIndex].status = status;
      if (winnerId) t.matches[mIndex].winnerTeamId = winnerId;
      saveDB(db);
      return;
    }
  }
};

export const addRefereeTag = (matchId: string, tag: string) => {
    const db = getDB();
    for (const t of db.tournaments) {
        const m = t.matches.find(m => m.id === matchId);
        if (m) {
            if (!m.refereeNotes) m.refereeNotes = [];
            m.refereeNotes.push(tag);
            saveDB(db);
            return;
        }
    }
}

import { ScoreState } from "../types";

const POINTS = ['0', '15', '30', '40'];

export const addPoint = (currentScore: ScoreState, player: 1 | 2): ScoreState => {
  const nextScore = JSON.parse(JSON.stringify(currentScore)) as ScoreState;

  // Helper to get opponent
  const opponent = player === 1 ? 2 : 1;
  const pPoints = player === 1 ? 'p1Points' : 'p2Points';
  const oPoints = opponent === 1 ? 'p1Points' : 'p2Points';

  const pGames = player === 1 ? 'p1Games' : 'p2Games';
  const oGames = opponent === 1 ? 'p1Games' : 'p2Games';

  const pSets = player === 1 ? 'p1Sets' : 'p2Sets';

  if (nextScore.isTiebreak) {
    // Tiebreak Logic: 1, 2, 3... First to 7, win by 2
    let pScore = parseInt(nextScore[pPoints]);
    let oScore = parseInt(nextScore[oPoints]);
    pScore++;
    nextScore[pPoints] = pScore.toString();

    if (pScore >= 7 && pScore - oScore >= 2) {
      // Win Set (Tiebreak)
      nextScore[pGames]++;
      nextScore[pSets]++;
      // Reset for next set
      nextScore.p1Games = 0;
      nextScore.p2Games = 0;
      nextScore.p1Points = '0';
      nextScore.p2Points = '0';
      nextScore.currentSet++;
      nextScore.isTiebreak = false;
    }
    return nextScore;
  }

  // Normal Game Logic
  const currentP = nextScore[pPoints];
  const currentO = nextScore[oPoints];

  if (currentP === '40') {
    if (currentO === '40') {
      // Deuce -> Advantage
      nextScore[pPoints] = 'AD';
    } else if (currentO === 'AD') {
      // Opponent had Advantage -> Back to Deuce
      nextScore[oPoints] = '40';
    } else {
      // Win Game
      winGame(nextScore, player);
    }
  } else if (currentP === 'AD') {
    // Win Game
    winGame(nextScore, player);
  } else {
    // 0 -> 15 -> 30 -> 40
    const idx = POINTS.indexOf(currentP);
    nextScore[pPoints] = POINTS[idx + 1];
  }

  return nextScore;
};

const winGame = (score: ScoreState, player: 1 | 2) => {
  score.p1Points = '0';
  score.p2Points = '0';

  const pGames = player === 1 ? 'p1Games' : 'p2Games';
  const oGames = player === 1 ? 'p2Games' : 'p1Games';
  const pSets = player === 1 ? 'p1Sets' : 'p2Sets';

  score[pGames]++;

  const myGames = score[pGames];
  const otherGames = score[oGames];

  // Set Win Logic: First to 6, win by 2
  if (myGames === 6 && otherGames <= 4) {
    score[pSets]++;
    resetSet(score);
  } else if (myGames === 7) {
     score[pSets]++;
     resetSet(score);
  } else if (myGames === 6 && otherGames === 6) {
    // Tiebreaker trigger
    score.isTiebreak = true;
    score.p1Points = '0';
    score.p2Points = '0'; // Tiebreak points start at 0 (displayed as integers usually)
  }
};

const resetSet = (score: ScoreState) => {
  score.p1Games = 0;
  score.p2Games = 0;
  score.currentSet++;
  score.isTiebreak = false;
};

