                    <span>{match.roundName}</span>
                    <span>{match.court}</span>
                </div>

                {/* Score Display */}
                <div className="grid grid-cols-[1fr_auto_1fr] gap-8 items-center">
                    <div className="text-right">
                        <div className="text-2xl font-bold text-white mb-1">{t1.name}</div>
                        <div className="text-sm text-gray-500">{t1.player1.name} / {t1.player2.name}</div>
                    </div>

                    <div className="flex gap-4 items-center bg-[#0A1628] px-6 py-4 rounded-xl border border-gray-700">
                        <div className="text-center">
                            <div className="text-xs text-gray-500 mb-1">SETS</div>
                            <div className="text-2xl font-bold text-white">{localScore.p1Sets}-{localScore.p2Sets}</div>
                        </div>
                        <div className="h-8 w-[1px] bg-gray-700"></div>
                         <div className="text-center">
                            <div className="text-xs text-gray-500 mb-1">GAMES</div>
                            <div className="text-2xl font-bold text-white">{localScore.p1Games}-{localScore.p2Games}</div>
                        </div>
                         <div className="h-8 w-[1px] bg-gray-700"></div>
                        <div className="text-center min-w-[60px]">
                            <div className="text-xs text-[#E67E50] mb-1 font-bold">POINTS</div>
                            <div className="text-4xl font-mono font-bold text-[#E67E50]">
                                {localScore.isTiebreak ? `${localScore.p1Points}-${localScore.p2Points}` : `${localScore.p1Points}-${localScore.p2Points}`}
                            </div>
                        </div>
                    </div>

                    <div className="text-left">
                        <div className="text-2xl font-bold text-white mb-1">{t2.name}</div>
                        <div className="text-sm text-gray-500">{t2.player1.name} / {t2.player2.name}</div>
                    </div>
                </div>
            </div>

            {/* Controls */}
            {status !== MatchStatus.COMPLETED ? (
                <div className="grid grid-cols-2 gap-6 h-64">
                    {/* Team 1 Controls */}
                    <div className="flex flex-col gap-3">
                         <div className="text-center text-indigo-300 font-bold mb-1">{t1.name}</div>
                         <button 
                            onClick={() => handlePoint(1, 1)}
                            className="flex-1 bg-indigo-600/90 hover:bg-indigo-600 active:scale-95 transition-all rounded-xl flex items-center justify-center p-4 shadow-lg border border-indigo-500/30 group"
                        >
                            <div className="text-center">
                                <div className="text-indigo-200 text-xs uppercase tracking-wider mb-1 group-hover:text-white transition-colors">Point</div>
                                <div className="text-xl font-bold text-white">{t1.player1.name}</div>
                            </div>
                        </button>
                        <button 
                            onClick={() => handlePoint(1, 2)}
                            className="flex-1 bg-indigo-600/90 hover:bg-indigo-600 active:scale-95 transition-all rounded-xl flex items-center justify-center p-4 shadow-lg border border-indigo-500/30 group"
                        >
                            <div className="text-center">
                                <div className="text-indigo-200 text-xs uppercase tracking-wider mb-1 group-hover:text-white transition-colors">Point</div>
                                <div className="text-xl font-bold text-white">{t1.player2.name}</div>
                            </div>
                        </button>
                    </div>

                    {/* Team 2 Controls */}
                    <div className="flex flex-col gap-3">
                         <div className="text-center text-emerald-300 font-bold mb-1">{t2.name}</div>
                         <button 
                            onClick={() => handlePoint(2, 1)}
                            className="flex-1 bg-emerald-600/90 hover:bg-emerald-600 active:scale-95 transition-all rounded-xl flex items-center justify-center p-4 shadow-lg border border-emerald-500/30 group"
                        >
                            <div className="text-center">
                                <div className="text-emerald-200 text-xs uppercase tracking-wider mb-1 group-hover:text-white transition-colors">Point</div>
                                <div className="text-xl font-bold text-white">{t2.player1.name}</div>
                            </div>
                        </button>
                         <button 
                            onClick={() => handlePoint(2, 2)}
                            className="flex-1 bg-emerald-600/90 hover:bg-emerald-600 active:scale-95 transition-all rounded-xl flex items-center justify-center p-4 shadow-lg border border-emerald-500/30 group"
                        >
                            <div className="text-center">
