                    <p className="text-gray-400 mb-6">Winner: {match.winnerTeamId === t1.id ? t1.name : t2.name}</p>
                    <button onClick={onShowBanner} className="bg-[#E67E50] text-white px-8 py-3 rounded-xl font-bold hover:bg-[#ff8f5d]">
                        Generate Winner Banner
                    </button>
                </div>
            )}

            {/* Quick Comments */}
             <div className="mt-8">
                <h4 className="text-sm font-medium text-gray-400 mb-3 uppercase tracking-wider">Referee Comments</h4>
                <div className="flex flex-wrap gap-2">
                    {["Excellent Serve", "Great Defense", "Powerful Smash", "Nice Net Play", "Clutch Performance", "Fair Play"].map(tag => (
                        <button key={tag} onClick={() => handleTag(tag)} className="px-4 py-2 bg-[#0A1628] border border-gray-700 rounded-lg text-sm text-gray-300 hover:border-[#E67E50] hover:text-[#E67E50]">
                            {tag}
                        </button>
                    ))}
                </div>
            </div>
        </div>
    )
}

// Winner Banner Component
const WinnerBanner = ({ match, tournamentName, teams, onClose }: any) => {
