import React, { useState } from 'react';
import { TournamentFormat, SkillLevel, Tournament, RegistrationStatus } from '../types';
import { createTournament, getCurrentTournament, updateTeamStatus, generateSchedule } from '../services/storage';
import { Check, X, Calendar, Users, Trophy, PlayCircle, Lock } from 'lucide-react';

export const AdminDashboard: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');

  const [activeTournament, setActiveTournament] = useState<Tournament | null>(getCurrentTournament());
  const [formData, setFormData] = useState({
    name: '',
    format: TournamentFormat.SINGLE_ELIMINATION,
    skillLevel: SkillLevel.INTERMEDIATE,
    maxTeams: 8,
    entryFee: 100,
    prizeMoney: 1000,
    refereePasscode: '1234',
    courts: 'Court 1, Court 2',
    registrationDeadline: ''
  });

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (loginEmail === 'zayam@test.com' && loginPassword === 'Test123!@#') {
        setIsAuthenticated(true);
    } else {
        alert("Invalid credentials. Please use the testing account.");
    }
  };

  const handleCreate = () => {
    if (!formData.name) return alert("Name is required");
    const t = createTournament({
      name: formData.name,
      format: formData.format,
      skillLevel: formData.skillLevel,
      maxTeams: formData.maxTeams,
      entryFee: formData.entryFee,
      prizeMoney: formData.prizeMoney,
      refereePasscode: formData.refereePasscode,
      courts: formData.courts.split(',').map(s => s.trim()),
      registrationDeadline: formData.registrationDeadline || new Date().toISOString()
    });
    setActiveTournament(t);
  };

  const handleTeamAction = (teamId: string, status: 'ACCEPTED' | 'REJECTED') => {
    if (!activeTournament) return;
    updateTeamStatus(activeTournament.id, teamId, status);
    setActiveTournament(getCurrentTournament());
  };

  const handleGenerateSchedule = () => {
    if (!activeTournament) return;
    generateSchedule(activeTournament.id);
    setActiveTournament(getCurrentTournament());
    alert("Schedule generated successfully!");
  }

  if (!isAuthenticated) {
    return (
        <div className="max-w-md mx-auto mt-20 p-8 bg-[#0F213A] rounded-2xl border border-gray-800 shadow-2xl">
            <div className="flex justify-center mb-6">
                <div className="h-16 w-16 bg-[#E67E50]/20 rounded-full flex items-center justify-center text-[#E67E50] shadow-[0_0_15px_rgba(230,126,80,0.3)]">
                    <Lock size={32} />
                </div>
            </div>
            <h2 className="text-2xl font-bold text-center text-white mb-2">Admin Portal</h2>
            <p className="text-gray-400 text-center text-sm mb-8">Please sign in to manage tournaments</p>

            <form onSubmit={handleLogin} className="space-y-4">
                <div>
                    <label className="block text-xs font-medium text-gray-400 mb-1 uppercase tracking-wider">Email Address</label>
                    <input 
                        type="email"
                        className="w-full bg-[#0A1628] border border-gray-700 rounded-xl p-3 text-white focus:border-[#E67E50] focus:ring-1 focus:ring-[#E67E50] focus:outline-none transition-all"
                        value={loginEmail}
                        onChange={e => setLoginEmail(e.target.value)}
                        placeholder="admin@matchup.com"
                    />
                </div>
                <div>
                    <label className="block text-xs font-medium text-gray-400 mb-1 uppercase tracking-wider">Password</label>
                    <input 
                        type="password"
                        className="w-full bg-[#0A1628] border border-gray-700 rounded-xl p-3 text-white focus:border-[#E67E50] focus:ring-1 focus:ring-[#E67E50] focus:outline-none transition-all"
                        value={loginPassword}
                        onChange={e => setLoginPassword(e.target.value)}
                        placeholder="••••••••"
                    />
                </div>
                <button className="w-full bg-[#E67E50] hover:bg-[#ff8f5d] text-white font-bold py-4 rounded-xl transition-all shadow-lg shadow-orange-500/20 mt-4">
                    Sign In
                </button>
            </form>
        </div>
    );
  }

  // View: Create Tournament
  if (!activeTournament) {
    return (
      <div className="bg-[#0F213A] p-8 rounded-2xl border border-gray-800 shadow-xl max-w-2xl mx-auto">
        <h2 className="text-2xl font-bold mb-6 text-white flex items-center gap-2">
           <Trophy className="text-[#E67E50]" /> Create Tournament
        </h2>
        <div className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-400 mb-2">Tournament Name</label>
            <input 
              className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:border-[#E67E50] focus:outline-none"
              placeholder="Summer Padel Open 2024"
              value={formData.name}
              onChange={e => setFormData({...formData, name: e.target.value})}
            />
          </div>
          <div className="grid grid-cols-2 gap-4">
             <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Format</label>
                <select 
                    className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:outline-none"
                    value={formData.format}
                    onChange={e => setFormData({...formData, format: e.target.value as TournamentFormat})}
                >
                    {Object.values(TournamentFormat).map(f => <option key={f} value={f}>{f.replace('_', ' ')}</option>)}
                </select>
             </div>
             <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">Skill Level</label>
                 <select 
                    className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:outline-none"
                    value={formData.skillLevel}
                    onChange={e => setFormData({...formData, skillLevel: e.target.value as SkillLevel})}
                >
                    {Object.values(SkillLevel).map(f => <option key={f} value={f}>{f}</option>)}
                </select>
             </div>
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-400 mb-2">Max Teams</label>
              <input 
                type="number"
                className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:outline-none"
                value={formData.maxTeams}
                onChange={e => setFormData({...formData, maxTeams: parseInt(e.target.value)})}
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-400 mb-2">Referee Passcode</label>
              <input 
                className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:outline-none"
                value={formData.refereePasscode}
                onChange={e => setFormData({...formData, refereePasscode: e.target.value})}
              />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-400 mb-2">Courts (comma separated)</label>
            <input 
              className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:outline-none"
              placeholder="Center Court, Court 2, Court 3"
              value={formData.courts}
              onChange={e => setFormData({...formData, courts: e.target.value})}
            />
          </div>

          <button 
            onClick={handleCreate}
            className="w-full bg-[#E67E50] hover:bg-[#ff8f5d] text-white font-bold py-4 rounded-xl transition-all shadow-lg shadow-orange-500/20"
          >
            Create Tournament
          </button>
        </div>
      </div>
    );
  }

  // View: Tournament Dashboard
  const pendingTeams = activeTournament.teams.filter(t => t.status === RegistrationStatus.PENDING);
  const acceptedTeams = activeTournament.teams.filter(t => t.status === RegistrationStatus.ACCEPTED);

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="bg-[#0F213A] p-6 rounded-2xl border border-gray-800 flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-white mb-2">{activeTournament.name}</h2>
          <div className="flex gap-4 text-sm text-gray-400">
             <span className="flex items-center gap-1"><Users size={16}/> {acceptedTeams.length} / {activeTournament.maxTeams} Teams</span>
             <span className="flex items-center gap-1"><Calendar size={16}/> {activeTournament.format.replace('_', ' ')}</span>
          </div>
        </div>
        <div className="flex gap-3">
          {activeTournament.matches.length === 0 && (
             <button 
             onClick={handleGenerateSchedule}
             disabled={acceptedTeams.length < 2}
             className="bg-indigo-600 hover:bg-indigo-700 disabled:bg-gray-700 text-white px-6 py-3 rounded-lg font-medium flex items-center gap-2 transition-colors"
           >
             <PlayCircle size={20} /> Generate Bracket
           </button>
          )}
          <div className="bg-[#0A1628] px-4 py-2 rounded-lg border border-gray-700">
            <span className="text-xs text-gray-500 uppercase">Passcode</span>
            <div className="font-mono text-[#E67E50] font-bold">{activeTournament.refereePasscode}</div>
          </div>
        </div>
      </div>

      <div className="grid md:grid-cols-2 gap-8">
        {/* Pending Requests */}
        <div className="bg-[#0F213A] rounded-2xl border border-gray-800 p-6">
          <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-2">
            <div className="w-2 h-2 rounded-full bg-yellow-500"></div>
            Pending Requests ({pendingTeams.length})
          </h3>
          <div className="space-y-3">
            {pendingTeams.length === 0 && <p className="text-gray-500 italic">No pending requests.</p>}
            {pendingTeams.map(team => (
              <div key={team.id} className="bg-[#0A1628] p-4 rounded-xl border border-gray-700 flex justify-between items-center group">
                <div>
                  <div className="font-bold text-white">{team.name}</div>
                  <div className="text-xs text-gray-400">{team.player1.name} & {team.player2.name}</div>
                </div>
                <div className="flex gap-2">
                  <button onClick={() => handleTeamAction(team.id, 'ACCEPTED')} className="p-2 bg-green-500/10 text-green-500 rounded-lg hover:bg-green-500/20"><Check size={18}/></button>
                  <button onClick={() => handleTeamAction(team.id, 'REJECTED')} className="p-2 bg-red-500/10 text-red-500 rounded-lg hover:bg-red-500/20"><X size={18}/></button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Accepted Teams */}
        <div className="bg-[#0F213A] rounded-2xl border border-gray-800 p-6">
          <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-2">
            <div className="w-2 h-2 rounded-full bg-green-500"></div>
            Accepted Teams ({acceptedTeams.length})
          </h3>
           <div className="space-y-3">
            {acceptedTeams.length === 0 && <p className="text-gray-500 italic">No teams accepted yet.</p>}
            {acceptedTeams.map(team => (
              <div key={team.id} className="bg-[#0A1628] p-4 rounded-xl border border-gray-700 flex justify-between items-center">
                <div>
                  <div className="font-bold text-white">{team.name}</div>
                  <div className="text-xs text-gray-400">{team.player1.name} & {team.player2.name}</div>
                </div>
                <div className="px-3 py-1 rounded-full bg-green-500/10 text-green-500 text-xs font-medium border border-green-500/20">
                  Ready
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { getCurrentTournament } from '../services/storage';
import { MatchStatus } from '../types';
import { Clock, Radio } from 'lucide-react';

export const LiveScoreboard: React.FC = () => {
  const [tournament, setTournament] = useState(getCurrentTournament());

  // Polling for live updates
  useEffect(() => {
    const interval = setInterval(() => {
        setTournament(getCurrentTournament());
    }, 2000); // 2 second refresh
    return () => clearInterval(interval);
  }, []);

  if (!tournament) {
    return (
        <div className="flex flex-col items-center justify-center h-96 text-center">
            <h1 className="text-4xl font-bold text-white mb-4">Match Up</h1>
            <p className="text-gray-400">No active tournament currently.</p>
        </div>
    );
  }

  const liveMatches = tournament.matches.filter(m => m.status === MatchStatus.IN_PROGRESS);
  const upcomingMatches = tournament.matches.filter(m => m.status === MatchStatus.SCHEDULED).sort((a,b) => a.scheduledTime.localeCompare(b.scheduledTime));
  const completedMatches = tournament.matches.filter(m => m.status === MatchStatus.COMPLETED).reverse();

  return (
    <div className="space-y-12">
        <div className="text-center mb-8">
            <h1 className="text-3xl md:text-5xl font-bold text-white mb-2">{tournament.name}</h1>
            <p className="text-[#E67E50] font-medium tracking-wide">LIVE TOURNAMENT FEED</p>
        </div>

      {/* LIVE SECTION */}
      <section>
        <div className="flex items-center gap-2 mb-6">
            <span className="relative flex h-3 w-3">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                <span className="relative inline-flex rounded-full h-3 w-3 bg-red-500"></span>
            </span>
            <h2 className="text-xl font-bold text-white">Live Action</h2>
        </div>

        {liveMatches.length === 0 ? (
            <div className="p-8 bg-[#0F213A] rounded-2xl border border-gray-800 text-center text-gray-500">
                No matches currently in progress.
            </div>
        ) : (
            <div className="grid md:grid-cols-2 gap-6">
                {liveMatches.map(match => (
                    <LiveMatchCard key={match.id} match={match} teams={tournament.teams} />
                ))}
            </div>
        )}
      </section>

      {/* SCHEDULE */}
      <div className="grid lg:grid-cols-2 gap-12">
        <section>
             <h2 className="text-xl font-bold text-white mb-6 flex items-center gap-2"><Clock size={20}/> Upcoming</h2>
             <div className="space-y-3">
                 {upcomingMatches.length === 0 && <p className="text-gray-500">No upcoming matches.</p>}
                 {upcomingMatches.map(match => (
                     <MatchRow key={match.id} match={match} teams={tournament.teams} />
                 ))}
             </div>
        </section>

        <section>
             <h2 className="text-xl font-bold text-white mb-6 text-gray-400">Completed</h2>
             <div className="space-y-3">
                 {completedMatches.length === 0 && <p className="text-gray-500">No completed matches.</p>}
                 {completedMatches.map(match => (
                     <MatchRow key={match.id} match={match} teams={tournament.teams} />
                 ))}
             </div>
        </section>
      </div>
    </div>
  );
};

const LiveMatchCard = ({ match, teams }: any) => {
    const t1 = teams.find((t: any) => t.id === match.team1Id);
    const t2 = teams.find((t: any) => t.id === match.team2Id);

    return (
        <div className="bg-gradient-to-br from-[#0F213A] to-[#162a45] rounded-2xl p-6 border border-[#E67E50] shadow-lg shadow-orange-900/10 relative overflow-hidden">
             {/* Background Element */}
            <div className="absolute -right-10 -top-10 w-40 h-40 bg-[#E67E50]/5 rounded-full blur-3xl"></div>

            <div className="flex justify-between items-start mb-6">
                 <span className="bg-red-500 text-white text-[10px] font-bold px-2 py-1 rounded animate-pulse">LIVE</span>
                 <span className="text-sm text-gray-400 font-medium">{match.court}</span>
            </div>

            <div className="flex items-center justify-between gap-4">
                <div className="flex-1">
                    <div className="text-2xl font-bold text-white leading-tight">{t1.name}</div>
                </div>
                <div className="text-center px-4">
                    <div className="text-4xl font-mono font-bold text-[#E67E50] tracking-tighter">
                        {match.score.p1Sets}-{match.score.p2Sets}
                    </div>
                    <div className="text-xs text-gray-500 uppercase mt-1">Sets</div>
                </div>
                <div className="flex-1 text-right">
                    <div className="text-2xl font-bold text-white leading-tight">{t2.name}</div>
                </div>
            </div>

            {/* Detailed Score */}
            <div className="mt-6 pt-4 border-t border-gray-700/50 flex justify-center gap-8 text-sm">
                 <div className="text-center">
                    <div className="text-gray-500 mb-1">Games</div>
                    <div className="font-mono text-white text-lg">{match.score.p1Games} - {match.score.p2Games}</div>
                 </div>
                 <div className="text-center">
                    <div className="text-gray-500 mb-1">Points</div>
                    <div className="font-mono text-[#E67E50] font-bold text-lg">{match.score.p1Points} - {match.score.p2Points}</div>
                 </div>
            </div>
        </div>
    )
}

const MatchRow = ({ match, teams }: any) => {
    const t1 = teams.find((t: any) => t.id === match.team1Id);
    const t2 = teams.find((t: any) => t.id === match.team2Id);

    // If teams are not decided yet (bracket placeholders)
    if (!t1 || !t2) return (
         <div className="bg-[#0F213A] p-4 rounded-xl border border-gray-800 flex justify-between items-center opacity-50">
            <span className="text-gray-500 italic">{match.roundName}</span>
            <span className="text-xs text-gray-600">TBD vs TBD</span>
        </div>
    );

    return (
        <div className="bg-[#0F213A] p-4 rounded-xl border border-gray-800 hover:bg-[#132640] transition-colors">
            <div className="flex justify-between items-center">
                <div className="flex-1">
                     <div className="flex justify-between items-center mb-1">
                        <div className={`font-bold ${match.winnerTeamId === t1.id ? 'text-[#E67E50]' : 'text-white'}`}>{t1.name}</div>
                        <div className="font-mono text-gray-400">{match.score.p1Sets}</div>
                     </div>
                     <div className="flex justify-between items-center">
                        <div className={`font-bold ${match.winnerTeamId === t2.id ? 'text-[#E67E50]' : 'text-white'}`}>{t2.name}</div>
                        <div className="font-mono text-gray-400">{match.score.p2Sets}</div>
                     </div>
                </div>
                <div className="ml-4 pl-4 border-l border-gray-700 text-xs text-gray-500 w-24 text-right">
                    {match.status === MatchStatus.COMPLETED ? 'Final' : new Date(match.scheduledTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                </div>
            </div>
            {match.refereeNotes && match.refereeNotes.length > 0 && (
                <div className="flex flex-wrap gap-1.5 mt-3 pt-2 border-t border-gray-700/30">
                    {match.refereeNotes.map((tag: string, i: number) => (
                        <span key={i} className="text-[10px] font-medium text-[#E67E50] bg-[#E67E50]/10 px-2 py-0.5 rounded border border-[#E67E50]/20 uppercase tracking-wide">
                            {tag}
                        </span>
                    ))}
                </div>
            )}
        </div>
    )
}

import React, { useState, useRef } from 'react';
import { getCurrentTournament, registerTeam } from '../services/storage';
import { User, Phone, Mail, Users, CheckCircle, Camera } from 'lucide-react';

export const RegistrationForm: React.FC = () => {
  const tournament = getCurrentTournament();
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);

  const [formData, setFormData] = useState({
    teamName: '',
    p1Name: '', p1Phone: '', p1Email: '', p1Photo: '',
    p2Name: '', p2Phone: '', p2Email: '', p2Photo: ''
  });

  const p1FileInputRef = useRef<HTMLInputElement>(null);
  const p2FileInputRef = useRef<HTMLInputElement>(null);

  if (!tournament) {
    return (
      <div className="flex items-center justify-center h-64 text-gray-400">
        No active tournament open for registration.
      </div>
    );
  }

  // Helper to compress and convert image to base64
  const processImage = (file: File, callback: (base64: string) => void) => {
    const reader = new FileReader();
    reader.onload = (e) => {
        const img = new Image();
        img.onload = () => {
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            // Resize to max 300px width to save LocalStorage space
            const MAX_WIDTH = 300;
            const scaleSize = MAX_WIDTH / img.width;
            canvas.width = MAX_WIDTH;
            canvas.height = img.height * scaleSize;

            if (ctx) {
                ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
                // Compress to JPEG 0.7 quality
                callback(canvas.toDataURL('image/jpeg', 0.7));
            }
        };
        img.src = e.target?.result as string;
    };
    reader.readAsDataURL(file);
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>, player: 'p1' | 'p2') => {
    if (e.target.files && e.target.files[0]) {
        processImage(e.target.files[0], (base64) => {
            if (player === 'p1') setFormData(prev => ({ ...prev, p1Photo: base64 }));
            else setFormData(prev => ({ ...prev, p2Photo: base64 }));
        });
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    // Simulate API delay
    setTimeout(() => {
        try {
            registerTeam(tournament.id, {
                name: formData.teamName,
                player1: { 
                    name: formData.p1Name, 
                    phone: formData.p1Phone, 
                    email: formData.p1Email, 
                    verified: true,
                    photoUrl: formData.p1Photo 
                },
                player2: { 
                    name: formData.p2Name, 
                    phone: formData.p2Phone, 
                    email: formData.p2Email, 
                    verified: true,
                    photoUrl: formData.p2Photo
                }
            });
            setSubmitted(true);
        } catch (err) {
            alert("Error registering team. Storage might be full.");
            console.error(err);
        }
        setLoading(false);
    }, 1000);
  };

  if (submitted) {
    return (
        <div className="max-w-md mx-auto mt-10 text-center p-8 bg-[#0F213A] rounded-2xl border border-green-900/50">
            <div className="w-16 h-16 bg-green-500/20 text-green-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <CheckCircle size={32} />
            </div>
            <h2 className="text-2xl font-bold text-white mb-2">Registration Received!</h2>
            <p className="text-gray-400">
                Team <span className="text-[#E67E50]">{formData.teamName}</span> has been submitted for approval. 
                You will receive an SMS when the admin accepts your request.
            </p>
            <button 
                onClick={() => { setSubmitted(false); setFormData({ teamName: '', p1Name: '', p1Phone: '', p1Email: '', p1Photo: '', p2Name: '', p2Phone: '', p2Email: '', p2Photo: '' })}}
                className="mt-8 text-sm text-gray-500 hover:text-white underline"
            >
                Register another team
            </button>
        </div>
    )
  }

  return (
    <div className="max-w-2xl mx-auto">
      <div className="text-center mb-10">
        <h1 className="text-3xl font-bold text-white mb-2">Join {tournament.name}</h1>
        <p className="text-gray-400">Format: {tournament.format.replace('_', ' ')} • Entry: ${tournament.entryFee}</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Team Info */}
        <div className="bg-[#0F213A] p-6 rounded-2xl border border-gray-800">
          <h3 className="text-lg font-medium text-white mb-4 flex items-center gap-2">
            <Users className="text-[#E67E50]" size={20} /> Team Details
          </h3>
          <div>
            <label className="block text-sm text-gray-400 mb-2">Team Name</label>
            <input 
              required
              value={formData.teamName}
              onChange={e => setFormData({...formData, teamName: e.target.value})}
              className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:border-[#E67E50] focus:outline-none"
              placeholder="e.g. The Match Up Pros"
            />
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-6">
            {/* Player 1 */}
            <div className="bg-[#0F213A] p-6 rounded-2xl border border-gray-800">
                <div className="flex justify-between items-start mb-4">
                    <h3 className="text-lg font-medium text-white flex items-center gap-2">
                        <User className="text-[#E67E50]" size={20} /> Player 1
                    </h3>
                    <div 
                        onClick={() => p1FileInputRef.current?.click()}
                        className="w-12 h-12 rounded-full bg-[#0A1628] border border-dashed border-gray-600 flex items-center justify-center cursor-pointer hover:border-[#E67E50] overflow-hidden"
                    >
                        {formData.p1Photo ? (
                            <img src={formData.p1Photo} alt="P1" className="w-full h-full object-cover" />
                        ) : (
                            <Camera size={16} className="text-gray-500" />
                        )}
                        <input 
                            type="file" 
                            ref={p1FileInputRef} 
                            onChange={(e) => handleImageChange(e, 'p1')} 
                            className="hidden" 
                            accept="image/*"
                        />
                    </div>
                </div>
                <div className="space-y-4">
                    <Input label="Full Name" value={formData.p1Name} onChange={(v: string) => setFormData({...formData, p1Name: v})} icon={<User size={16}/>} />
                    <Input label="Phone" value={formData.p1Phone} onChange={(v: string) => setFormData({...formData, p1Phone: v})} icon={<Phone size={16}/>} />
                    <Input label="Email" value={formData.p1Email} onChange={(v: string) => setFormData({...formData, p1Email: v})} icon={<Mail size={16}/>} />
                </div>
            </div>

            {/* Player 2 */}
            <div className="bg-[#0F213A] p-6 rounded-2xl border border-gray-800">
                <div className="flex justify-between items-start mb-4">
                    <h3 className="text-lg font-medium text-white flex items-center gap-2">
                        <User className="text-[#E67E50]" size={20} /> Player 2
                    </h3>
                    <div 
                        onClick={() => p2FileInputRef.current?.click()}
                        className="w-12 h-12 rounded-full bg-[#0A1628] border border-dashed border-gray-600 flex items-center justify-center cursor-pointer hover:border-[#E67E50] overflow-hidden"
                    >
                        {formData.p2Photo ? (
                            <img src={formData.p2Photo} alt="P2" className="w-full h-full object-cover" />
                        ) : (
                            <Camera size={16} className="text-gray-500" />
                        )}
                         <input 
                            type="file" 
                            ref={p2FileInputRef} 
                            onChange={(e) => handleImageChange(e, 'p2')} 
                            className="hidden" 
                            accept="image/*"
                        />
                    </div>
                </div>
                <div className="space-y-4">
                    <Input label="Full Name" value={formData.p2Name} onChange={(v: string) => setFormData({...formData, p2Name: v})} icon={<User size={16}/>} />
                    <Input label="Phone" value={formData.p2Phone} onChange={(v: string) => setFormData({...formData, p2Phone: v})} icon={<Phone size={16}/>} />
                    <Input label="Email" value={formData.p2Email} onChange={(v: string) => setFormData({...formData, p2Email: v})} icon={<Mail size={16}/>} />
                </div>
            </div>
        </div>

        <button 
            type="submit"
            disabled={loading}
            className="w-full bg-[#E67E50] hover:bg-[#ff8f5d] disabled:bg-gray-700 text-white font-bold py-4 rounded-xl transition-all shadow-lg shadow-orange-500/20"
        >
            {loading ? 'Submitting...' : 'Complete Registration'}
        </button>
      </form>
    </div>
  );
};

const Input = ({ label, value, onChange, icon }: any) => (
    <div>
        <label className="block text-xs text-gray-500 mb-1">{label}</label>
        <div className="relative">
            <span className="absolute left-3 top-3.5 text-gray-600">{icon}</span>
            <input 
                required
                value={value}
                onChange={e => onChange(e.target.value)}
                className="w-full bg-[#0A1628] border border-gray-700 rounded-lg py-3 pl-10 pr-3 text-white text-sm focus:border-[#E67E50] focus:outline-none"
            />
        </div>
    </div>
);

import React, { useState, useEffect } from 'react';
import { getCurrentTournament, updateMatchScore, addRefereeTag } from '../services/storage';
import { addPoint } from '../services/scoreEngine';
import { Match, MatchStatus } from '../types';
import { Lock, Play, RotateCcw, Award, Check, X, Trophy } from 'lucide-react';

export const RefereeInterface: React.FC = () => {
  const [tournament, setTournament] = useState(getCurrentTournament());
  const [passcode, setPasscode] = useState('');
  const [authenticated, setAuthenticated] = useState(false);
  const [selectedMatch, setSelectedMatch] = useState<Match | null>(null);

  // Winner Banner State
  const [showBanner, setShowBanner] = useState(false);

  // Authentication
  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (tournament && passcode === tournament.refereePasscode) {
      setAuthenticated(true);
    } else {
      alert("Invalid Passcode");
    }
  };

  if (!tournament) return <div className="p-8 text-center text-gray-500">No active tournament.</div>;

  if (!authenticated) {
    return (
      <div className="max-w-md mx-auto mt-20 p-8 bg-[#0F213A] rounded-2xl border border-gray-800">
        <div className="flex justify-center mb-6">
            <div className="h-12 w-12 bg-[#E67E50]/20 rounded-full flex items-center justify-center text-[#E67E50]">
                <Lock size={24} />
            </div>
        </div>
        <h2 className="text-xl font-bold text-center text-white mb-6">Referee Access</h2>
        <form onSubmit={handleLogin}>
          <input 
            type="password"
            placeholder="Enter Tournament Passcode"
            className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white mb-4 text-center tracking-widest text-lg"
            value={passcode}
            onChange={e => setPasscode(e.target.value)}
          />
          <button className="w-full bg-[#E67E50] text-white font-bold py-3 rounded-xl hover:bg-[#ff8f5d]">
            Enter
          </button>
        </form>
      </div>
    );
  }

  // Match List View
  if (!selectedMatch) {
    const matches = tournament.matches.sort((a, b) => new Date(a.scheduledTime).getTime() - new Date(b.scheduledTime).getTime());
    return (
      <div className="max-w-3xl mx-auto">
        <h2 className="text-2xl font-bold text-white mb-6">Scheduled Matches</h2>
        <div className="space-y-4">
          {matches.map(m => {
             const t1 = tournament.teams.find(t => t.id === m.team1Id);
             const t2 = tournament.teams.find(t => t.id === m.team2Id);
             if (!t1 || !t2) return null; // Skip placeholder matches

             return (
               <div key={m.id} onClick={() => setSelectedMatch(m)} className="bg-[#0F213A] p-4 rounded-xl border border-gray-800 hover:border-[#E67E50] cursor-pointer transition-colors group">
                 <div className="flex justify-between items-center mb-2">
                    <span className="text-xs font-medium text-[#E67E50] px-2 py-1 bg-[#E67E50]/10 rounded uppercase tracking-wider">{m.court}</span>
                    <span className="text-xs text-gray-500">{new Date(m.scheduledTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</span>
                 </div>
                 <div className="flex justify-between items-center">
                    <div className="text-lg font-bold text-white">{t1.name} vs {t2.name}</div>
                    {m.status === MatchStatus.IN_PROGRESS && <div className="animate-pulse w-2 h-2 bg-green-500 rounded-full"></div>}
                 </div>
                 <div className="text-sm text-gray-500 mt-1">{m.roundName}</div>
               </div>
             )
          })}
          {matches.length === 0 && <div className="text-gray-500">No matches scheduled with valid teams.</div>}
        </div>
      </div>
    );
  }

  return (
    <div>
        {showBanner && selectedMatch.winnerTeamId ? (
            <WinnerBanner 
                match={selectedMatch} 
                tournamentName={tournament.name}
                teams={tournament.teams}
                onClose={() => { setShowBanner(false); setSelectedMatch(null); }} 
            />
        ) : (
            <ScoringControl 
                match={selectedMatch} 
                teams={tournament.teams} 
                onUpdate={(m) => {
                    updateMatchScore(m.id, m.score, m.status, m.winnerTeamId);
                    setSelectedMatch(m);
                }}
                onBack={() => setSelectedMatch(null)}
                onShowBanner={() => setShowBanner(true)}
            />
        )}
    </div>
  );
};

// Internal Component for Match Scoring
const ScoringControl = ({ match, teams, onUpdate, onBack, onShowBanner }: any) => {
    const t1 = teams.find((t: any) => t.id === match.team1Id);
    const t2 = teams.find((t: any) => t.id === match.team2Id);
    const [localScore, setLocalScore] = useState(match.score);
    const [status, setStatus] = useState(match.status);

    const handlePoint = (team: 1 | 2, playerIdx: 1 | 2) => {
        if (status === MatchStatus.COMPLETED) return;
        if (status === MatchStatus.SCHEDULED) setStatus(MatchStatus.IN_PROGRESS);

        const newScore = addPoint(localScore, team, playerIdx);
        setLocalScore(newScore);

        // Check for match winner logic based on sets
        // Simple Padel: Best of 3 sets (First to 2 sets)
        let winnerId = undefined;
        let newStatus = status === MatchStatus.SCHEDULED ? MatchStatus.IN_PROGRESS : status;

        if (newScore.p1Sets === 2) {
            newStatus = MatchStatus.COMPLETED;
            winnerId = t1.id;
        } else if (newScore.p2Sets === 2) {
            newStatus = MatchStatus.COMPLETED;
            winnerId = t2.id;
        }

        onUpdate({ ...match, score: newScore, status: newStatus, winnerTeamId: winnerId });
        if (winnerId) setStatus(MatchStatus.COMPLETED);
    };

    const handleTag = (tag: string) => {
        addRefereeTag(match.id, tag);
        alert(`Tag "${tag}" added!`);
    };

    return (
        <div className="max-w-4xl mx-auto">
            <button onClick={onBack} className="mb-4 text-sm text-gray-500 hover:text-white">&larr; Back to List</button>

            {/* Scoreboard Header */}
            <div className="bg-[#0F213A] rounded-2xl border border-gray-800 p-6 mb-6">
                <div className="flex justify-between text-gray-400 text-sm mb-4 uppercase tracking-wider">
                    <span>{match.roundName}</span>
                    <span>{match.court}</span>
                </div>

                {/* Score Display */}
                <div className="grid grid-cols-[1fr_auto_1fr] gap-8 items-center">
                    <div className="text-right">
                        <div className="text-2xl font-bold text-white mb-1">{t1.name}</div>
                        <div className="text-sm text-gray-500">{t1.player1.name} / {t1.player2.name}</div>
                    </div>

                    <div className="flex gap-4 items-center bg-[#0A1628] px-6 py-4 rounded-xl border border-gray-700">
                        <div className="text-center">
                            <div className="text-xs text-gray-500 mb-1">SETS</div>
                            <div className="text-2xl font-bold text-white">{localScore.p1Sets}-{localScore.p2Sets}</div>
                        </div>
                        <div className="h-8 w-[1px] bg-gray-700"></div>
                         <div className="text-center">
                            <div className="text-xs text-gray-500 mb-1">GAMES</div>
                            <div className="text-2xl font-bold text-white">{localScore.p1Games}-{localScore.p2Games}</div>
                        </div>
                         <div className="h-8 w-[1px] bg-gray-700"></div>
                        <div className="text-center min-w-[60px]">
                            <div className="text-xs text-[#E67E50] mb-1 font-bold">POINTS</div>
