import React, { useState, useEffect } from 'react';
import { getCurrentTournament } from '../services/storage';
import { MatchStatus } from '../types';
import { Clock, Radio } from 'lucide-react';

export const LiveScoreboard: React.FC = () => {
  const [tournament, setTournament] = useState(getCurrentTournament());

  // Polling for live updates
  useEffect(() => {
    const interval = setInterval(() => {
        setTournament(getCurrentTournament());
    }, 2000); // 2 second refresh
    return () => clearInterval(interval);
  }, []);

  if (!tournament) {
    return (
        <div className="flex flex-col items-center justify-center h-[60vh] text-center px-4">
            <h1 className="text-4xl font-bold text-white mb-4">Match Up</h1>
            <p className="text-gray-400">No active tournament currently.</p>
        </div>
    );
  }

  const liveMatches = tournament.matches.filter(m => m.status === MatchStatus.IN_PROGRESS);
  const upcomingMatches = tournament.matches.filter(m => m.status === MatchStatus.SCHEDULED).sort((a,b) => a.scheduledTime.localeCompare(b.scheduledTime));
  const completedMatches = tournament.matches.filter(m => m.status === MatchStatus.COMPLETED).reverse();

  return (
    <div className="space-y-8 md:space-y-12 pb-20">
        <div className="text-center mb-6 md:mb-8">
            <h1 className="text-2xl md:text-5xl font-bold text-white mb-2">{tournament.name}</h1>
            <p className="text-[#E67E50] text-sm md:text-base font-medium tracking-wide">LIVE TOURNAMENT FEED</p>
        </div>

      {/* LIVE SECTION */}
      <section>
        <div className="flex items-center gap-2 mb-4 md:mb-6">
            <span className="relative flex h-3 w-3">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                <span className="relative inline-flex rounded-full h-3 w-3 bg-red-500"></span>
            </span>
            <h2 className="text-lg md:text-xl font-bold text-white">Live Action</h2>
        </div>

        {liveMatches.length === 0 ? (
            <div className="p-8 bg-[#0F213A] rounded-2xl border border-gray-800 text-center text-gray-500 text-sm">
                No matches currently in progress.
            </div>
        ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 md:gap-6">
                {liveMatches.map(match => (
                    <LiveMatchCard key={match.id} match={match} teams={tournament.teams} />
                ))}
            </div>
        )}
      </section>

      {/* SCHEDULE */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 md:gap-12">
        <section>
             <h2 className="text-lg md:text-xl font-bold text-white mb-4 md:mb-6 flex items-center gap-2"><Clock size={20}/> Upcoming</h2>
             <div className="space-y-3">
                 {upcomingMatches.length === 0 && <p className="text-gray-500 text-sm">No upcoming matches.</p>}
                 {upcomingMatches.map(match => (
                     <MatchRow key={match.id} match={match} teams={tournament.teams} />
                 ))}
             </div>
        </section>

        <section>
             <h2 className="text-lg md:text-xl font-bold text-white mb-4 md:mb-6 text-gray-400">Completed</h2>
             <div className="space-y-3">
                 {completedMatches.length === 0 && <p className="text-gray-500 text-sm">No completed matches.</p>}
                 {completedMatches.map(match => (
                     <MatchRow key={match.id} match={match} teams={tournament.teams} />
                 ))}
             </div>
        </section>
      </div>
    </div>
  );
};

