        </div>
        <div className="flex gap-3 w-full md:w-auto">
          <button 
            onClick={handleGenerateSchedule}
            disabled={acceptedTeams.length < 2}
            className={`${
              activeTournament.matches.length > 0 
                ? 'bg-amber-600 hover:bg-amber-700' 
                : 'bg-indigo-600 hover:bg-indigo-700'
