           <div className="space-y-3">
            {acceptedTeams.length === 0 && <p className="text-gray-500 italic text-sm">No teams accepted yet.</p>}
            {acceptedTeams.map(team => (
              <div key={team.id} className="bg-[#0A1628] p-3 md:p-4 rounded-xl border border-gray-700 flex justify-between items-center">
                <div>
                  <div className="font-bold text-white text-sm md:text-base">{team.name}</div>
                  <div className="text-xs text-gray-400">{team.player1.name} & {team.player2.name}</div>
                </div>
                <div className="px-2 py-1 md:px-3 md:py-1 rounded-full bg-green-500/10 text-green-500 text-[10px] md:text-xs font-medium border border-green-500/20">
                  Ready
                </div>
              </div>
            ))}
