const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

const initialScore: ScoreState = {
  p1Points: '0',
  p2Points: '0',
  p1Games: 0,
  p2Games: 0,
  p1Sets: 0,
  p2Sets: 0,
  currentSet: 1,
  isTiebreak: false,
  history: []
};

// --- READ OPERATIONS (SUBSCRIPTIONS) ---

export const subscribeToTournaments = (callback: (tournaments: Tournament[]) => void) => {
  const q = query(collection(db, "tournaments"), orderBy("createdAt", "desc"));
  return onSnapshot(q, (snapshot) => {
    const tournaments = snapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    })) as Tournament[];
    callback(tournaments);
  });
};

export const subscribeToTournament = (id: string, callback: (t: Tournament | null) => void) => {
  return onSnapshot(doc(db, "tournaments", id), (doc) => {
    if (doc.exists()) {
      callback({ id: doc.id, ...doc.data() } as Tournament);
    } else {
      callback(null);
    }
  });
};

// --- WRITE OPERATIONS ---

export const createTournament = async (t: Omit<Tournament, 'id' | 'teams' | 'matches' | 'status'>) => {
  try {
    const newTournament = {
      ...t,
      teams: [],
      matches: [],
      status: 'ACTIVE',
      createdAt: new Date().toISOString()
    };
    const docRef = await addDoc(collection(db, "tournaments"), newTournament);
    return docRef.id;
  } catch (e) {
    console.error("Error creating tournament: ", e);
    throw e;
  }
};

export const registerTeam = async (tournamentId: string, team: Omit<Team, 'id' | 'status' | 'registeredAt'>) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);

  if (!tSnap.exists()) throw new Error("Tournament not found");

  const currentData = tSnap.data() as Tournament;

  const newTeam: Team = {
    ...team,
    id: Math.random().toString(36).substr(2, 9),
    status: RegistrationStatus.PENDING,
    registeredAt: new Date().toISOString()
  };

  const updatedTeams = [...currentData.teams, newTeam];
  await updateDoc(tRef, { teams: updatedTeams });
};

export const updateTeamStatus = async (tournamentId: string, teamId: string, status: 'ACCEPTED' | 'REJECTED') => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);

  if (!tSnap.exists()) return;

  const currentData = tSnap.data() as Tournament;
  const updatedTeams = currentData.teams.map(t => {
    if (t.id === teamId) return { ...t, status: status as RegistrationStatus };
    return t;
  });

  await updateDoc(tRef, { teams: updatedTeams });
};

export const generateSchedule = async (tournamentId: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;

  const t = tSnap.data() as Tournament;
  const acceptedTeams = t.teams.filter(tm => tm.status === RegistrationStatus.ACCEPTED);

  const matches: Match[] = [];

  // Staggered Schedule Logic
  for (let i = 0; i < acceptedTeams.length; i += 2) {
    if (i + 1 < acceptedTeams.length) {
      matches.push({
        id: Math.random().toString(36).substr(2, 9),
        tournamentId,
        team1Id: acceptedTeams[i].id,
        team2Id: acceptedTeams[i + 1].id,
        round: 1, 
        roundName: acceptedTeams.length > 4 ? "Quarter Finals" : "Semi Finals",
        court: t.courts[i % t.courts.length] || "Court 1",
        scheduledTime: new Date(new Date().getTime() + (i * 60 * 60 * 1000)).toISOString(),
        status: MatchStatus.SCHEDULED,
        score: JSON.parse(JSON.stringify(initialScore))
      });
    }
  }

  // Placeholder Final
  matches.push({
      id: "final_match",
      tournamentId,
      team1Id: undefined,
      team2Id: undefined,
      round: 2,
      roundName: "Grand Final",
      court: "Court 1",
      scheduledTime: new Date(new Date().getTime() + (24 * 60 * 60 * 1000)).toISOString(),
      status: MatchStatus.SCHEDULED,
      score: JSON.parse(JSON.stringify(initialScore))
  });

  await updateDoc(tRef, { matches: matches });
};

export const updateMatchScore = async (tournamentId: string, matchId: string, newScore: ScoreState, status: MatchStatus, winnerId?: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;

  const t = tSnap.data() as Tournament;
  const updatedMatches = t.matches.map(m => {
      if (m.id === matchId) {
          return {
              ...m,
              score: newScore,
              status: status,
              winnerTeamId: winnerId
          };
      }
      return m;
  });

  await updateDoc(tRef, { matches: updatedMatches });
};

export const addRefereeTag = async (tournamentId: string, matchId: string, tag: string) => {
    const tRef = doc(db, "tournaments", tournamentId);
    const tSnap = await getDoc(tRef);
    if (!tSnap.exists()) return;

    const t = tSnap.data() as Tournament;
    const updatedMatches = t.matches.map(m => {
        if (m.id === matchId) {
            const notes = m.refereeNotes || [];
            return { ...m, refereeNotes: [...notes, tag] };
        }
        return m;
    });

    await updateDoc(tRef, { matches: updatedMatches });
};

import React, { useState, useEffect } from 'react';
