            </div>
            <div>
              <label className="block text-sm font-medium text-gray-400 mb-2">Referee Passcode</label>
              <input 
                className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:outline-none"
                value={formData.refereePasscode}
                onChange={e => setFormData({...formData, refereePasscode: e.target.value})}
              />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-400 mb-2">Courts (comma separated)</label>
            <input 
              className="w-full bg-[#0A1628] border border-gray-700 rounded-lg p-3 text-white focus:outline-none"
              placeholder="Center Court, Court 2, Court 3"
              value={formData.courts}
              onChange={e => setFormData({...formData, courts: e.target.value})}
            />
          </div>

          <button 
            onClick={handleCreate}
            className="w-full bg-[#E67E50] hover:bg-[#ff8f5d] text-white font-bold py-4 rounded-xl transition-all shadow-lg shadow-orange-500/20"
          >
            Create Tournament
          </button>
        </div>
      </div>
    );
  }

  // View: Tournament List
  if (!selectedTournamentId) {
      return (
          <div className="pb-20">
              <div className="flex justify-between items-center mb-8">
                  <h1 className="text-2xl md:text-3xl font-bold text-white">Tournaments</h1>
                  <button onClick={() => setIsCreating(true)} className="bg-[#E67E50] hover:bg-[#ff8f5d] text-white px-4 py-2 rounded-lg flex items-center gap-2 font-medium">
                      <Plus size={20}/> New Tournament
                  </button>
              </div>

              <div className="grid gap-4">
                {tournaments.length === 0 && <div className="text-gray-500 text-center py-10">No tournaments created yet.</div>}
                {tournaments.map(t => (
                    <div key={t.id} onClick={() => setSelectedTournamentId(t.id)} className="bg-[#0F213A] p-6 rounded-xl border border-gray-800 hover:border-[#E67E50] cursor-pointer group flex justify-between items-center">
                        <div>
                            <h3 className="text-xl font-bold text-white">{t.name}</h3>
                            <div className="text-sm text-gray-400 mt-1">Passcode: {t.refereePasscode} • {t.teams.length} Teams</div>
                        </div>
                        <ChevronRight className="text-gray-600 group-hover:text-white"/>
                    </div>
                ))}
              </div>
          </div>
      )
  }

  if (!activeTournament) return <div className="p-8 text-center text-gray-500">Loading...</div>;

  // View: Tournament Detail (Dashboard)
  const pendingTeams = activeTournament.teams.filter(t => t.status === RegistrationStatus.PENDING);
  const acceptedTeams = activeTournament.teams.filter(t => t.status === RegistrationStatus.ACCEPTED);

  return (
    <div className="space-y-6 md:space-y-8 pb-20">
      {/* Header */}
      <button onClick={() => setSelectedTournamentId(null)} className="text-gray-500 hover:text-white flex items-center gap-1">
          <ChevronLeft size={20}/> Back to All Tournaments
      </button>

      <div className="bg-[#0F213A] p-4 md:p-6 rounded-2xl border border-gray-800 flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h2 className="text-xl md:text-2xl font-bold text-white mb-2">{activeTournament.name}</h2>
          <div className="flex gap-4 text-xs md:text-sm text-gray-400">
             <span className="flex items-center gap-1"><Users size={16}/> {acceptedTeams.length} / {activeTournament.maxTeams} Teams</span>
             <span className="flex items-center gap-1"><Calendar size={16}/> {activeTournament.format.replace('_', ' ')}</span>
          </div>
        </div>
        <div className="flex gap-3 w-full md:w-auto">
          <button 
            onClick={handleGenerateSchedule}
            disabled={acceptedTeams.length < 2}
            className={`${
              activeTournament.matches.length > 0 
                ? 'bg-amber-600 hover:bg-amber-700' 
                : 'bg-indigo-600 hover:bg-indigo-700'
            } disabled:bg-gray-700 text-white px-4 py-2 md:px-6 md:py-3 rounded-lg font-medium flex items-center justify-center gap-2 transition-colors text-sm md:text-base flex-1 md:flex-none`}
          >
            {activeTournament.matches.length > 0 ? (
              <><RefreshCcw size={20} /> Regenerate Bracket</>
            ) : (
              <><PlayCircle size={20} /> Generate Bracket</>
            )}
          </button>
          <div className="bg-[#0A1628] px-4 py-2 rounded-lg border border-gray-700 flex flex-col items-center justify-center min-w-[80px]">
            <span className="text-[10px] text-gray-500 uppercase">Passcode</span>
            <div className="font-mono text-[#E67E50] font-bold text-lg">{activeTournament.refereePasscode}</div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 md:gap-8">
        {/* Pending Requests */}
        <div className="bg-[#0F213A] rounded-2xl border border-gray-800 p-4 md:p-6">
          <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-2">
            <div className="w-2 h-2 rounded-full bg-yellow-500"></div>
            Pending Requests ({pendingTeams.length})
          </h3>
          <div className="space-y-3">
            {pendingTeams.length === 0 && <p className="text-gray-500 italic text-sm">No pending requests.</p>}
            {pendingTeams.map(team => (
              <div key={team.id} className="bg-[#0A1628] p-3 md:p-4 rounded-xl border border-gray-700 flex justify-between items-center group">
                <div>
                  <div className="font-bold text-white text-sm md:text-base">{team.name}</div>
                  <div className="text-xs text-gray-400">{team.player1.name} & {team.player2.name}</div>
                </div>
                <div className="flex gap-2">
                  <button onClick={() => handleTeamAction(team.id, 'ACCEPTED')} className="p-2 bg-green-500/10 text-green-500 rounded-lg hover:bg-green-500/20"><Check size={18}/></button>
                  <button onClick={() => handleTeamAction(team.id, 'REJECTED')} className="p-2 bg-red-500/10 text-red-500 rounded-lg hover:bg-red-500/20"><X size={18}/></button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Accepted Teams */}
        <div className="bg-[#0F213A] rounded-2xl border border-gray-800 p-4 md:p-6">
          <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-2">
            <div className="w-2 h-2 rounded-full bg-green-500"></div>
            Accepted Teams ({acceptedTeams.length})
          </h3>
           <div className="space-y-3">
            {acceptedTeams.length === 0 && <p className="text-gray-500 italic text-sm">No teams accepted yet.</p>}
            {acceptedTeams.map(team => (
              <div key={team.id} className="bg-[#0A1628] p-3 md:p-4 rounded-xl border border-gray-700 flex justify-between items-center">
                <div>
                  <div className="font-bold text-white text-sm md:text-base">{team.name}</div>
                  <div className="text-xs text-gray-400">{team.player1.name} & {team.player2.name}</div>
                </div>
                <div className="px-2 py-1 md:px-3 md:py-1 rounded-full bg-green-500/10 text-green-500 text-[10px] md:text-xs font-medium border border-green-500/20">
                  Ready
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export enum TournamentFormat {
  SINGLE_ELIMINATION = 'SINGLE_ELIMINATION',
  DOUBLE_ELIMINATION = 'DOUBLE_ELIMINATION',
  ROUND_ROBIN = 'ROUND_ROBIN'
}

export enum SkillLevel {
  NEWCOMER = 'NEWCOMER',
  BEGINNER = 'BEGINNER',
  INTERMEDIATE = 'INTERMEDIATE',
  ADVANCED = 'ADVANCED',
  PROFESSIONAL = 'PROFESSIONAL'
}

export enum MatchStatus {
  SCHEDULED = 'SCHEDULED',
  IN_PROGRESS = 'IN_PROGRESS',
  COMPLETED = 'COMPLETED'
}

export enum RegistrationStatus {
  PENDING = 'PENDING',
  ACCEPTED = 'ACCEPTED',
  REJECTED = 'REJECTED'
}

export interface Player {
  name: string;
  phone: string;
  email: string;
  photoUrl?: string; // Base64 for MVP
  verified: boolean;
}

export interface Team {
  id: string;
  name: string;
  player1: Player;
  player2: Player;
  status: RegistrationStatus;
  registeredAt: string;
  // Stats
  matchesPlayed?: number;
  wins?: number; 
  losses?: number; 
  points?: number; // 3 for win, 0 for loss
  setsWon?: number;
  setsLost?: number;
}

export interface ScoreState {
  p1Points: string; // 0, 15, 30, 40, AD
  p2Points: string;
  p1Games: number;
  p2Games: number;
  p1Sets: number;
  p2Sets: number;
  currentSet: number; // 1, 2, 3
  isTiebreak: boolean;
  history: string[]; // Log of points
}

export interface Match {
  id: string;
  tournamentId: string;
  team1Id?: string; // Can be undefined in bracket initially
  team2Id?: string;
  stage?: 'GROUP' | 'PLAYOFF' | 'BRACKET'; // New field
  group?: string; // "A", "B" etc.
  round: number; // 1 = Final, 2 = Semi, 4 = Quarter, etc. (Inverse for easy logic) or 1,2,3 for sequential
  roundName: string; // "Quarter Finals", "Finals", "Group A"
  court: string;
  scheduledTime: string;
  status: MatchStatus;
  score: ScoreState;
  winnerTeamId?: string;
  refereeNotes?: string[]; // Skill tags
}

export interface Tournament {
  id: string;
  name: string;
  format: TournamentFormat;
  skillLevel: SkillLevel;
  maxTeams: number;
  entryFee: number;
  prizeMoney: number;
  refereePasscode: string;
  courts: string[]; // e.g., ["Court 1", "Court 2"]
  registrationDeadline: string;
  teams: Team[];
  matches: Match[];
  status: 'DRAFT' | 'ACTIVE' | 'COMPLETED';
}

export interface GlobalState {
  tournaments: Tournament[];
  currentTournamentId: string | null;
}

