// --- READ OPERATIONS (SUBSCRIPTIONS) ---

export const subscribeToTournaments = (callback: (tournaments: Tournament[]) => void) => {
  const q = query(collection(db, "tournaments"), orderBy("createdAt", "desc"));
  return onSnapshot(q, (snapshot) => {
    const tournaments = snapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    })) as Tournament[];
    callback(tournaments);
  });
};

export const subscribeToTournament = (id: string, callback: (t: Tournament | null) => void) => {
  return onSnapshot(doc(db, "tournaments", id), (doc) => {
    if (doc.exists()) {
      callback({ id: doc.id, ...doc.data() } as Tournament);
    } else {
      callback(null);
    }
  });
};

// --- WRITE OPERATIONS ---

export const createTournament = async (t: Omit<Tournament, 'id' | 'teams' | 'matches' | 'status'>) => {
  try {
    const newTournament = {
      ...t,
      teams: [],
      matches: [],
      status: 'ACTIVE',
      createdAt: new Date().toISOString()
    };
    const docRef = await addDoc(collection(db, "tournaments"), newTournament);
    return docRef.id;
  } catch (e) {
    console.error("Error creating tournament: ", e);
    throw e;
  }
};

export const registerTeam = async (tournamentId: string, team: Omit<Team, 'id' | 'status' | 'registeredAt'>) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);

  if (!tSnap.exists()) throw new Error("Tournament not found");

  const currentData = tSnap.data() as Tournament;

  const newTeam: Team = {
    ...team,
    id: Math.random().toString(36).substr(2, 9),
    status: RegistrationStatus.PENDING,
    registeredAt: new Date().toISOString(),
    wins: 0,
    losses: 0,
    points: 0,
    setsWon: 0,
    setsLost: 0,
    matchesPlayed: 0
  };

  const updatedTeams = [...currentData.teams, newTeam];
  await updateDoc(tRef, { teams: updatedTeams });
};

export const updateTeamStatus = async (tournamentId: string, teamId: string, status: 'ACCEPTED' | 'REJECTED') => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);

  if (!tSnap.exists()) return;

  const currentData = tSnap.data() as Tournament;
  const updatedTeams = currentData.teams.map(t => {
    if (t.id === teamId) return { ...t, status: status as RegistrationStatus };
    return t;
  });

  await updateDoc(tRef, { teams: updatedTeams });
};

export const generateSchedule = async (tournamentId: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;

  const t = tSnap.data() as Tournament;
  const acceptedTeams = t.teams.filter(tm => tm.status === RegistrationStatus.ACCEPTED);

  if (acceptedTeams.length < 2) return;

  let matches: Match[] = [];

  if (t.format === TournamentFormat.ROUND_ROBIN) {
    // ROUND ROBIN LOGIC
    // 1. Create Groups (Max 4 teams per group usually)
    const teamsPerGroup = 4;
    const numGroups = Math.ceil(acceptedTeams.length / teamsPerGroup);
    const groups: Team[][] = Array.from({ length: numGroups }, () => []);

    // Distribute teams into groups
    acceptedTeams.forEach((team, index) => {
        groups[index % numGroups].push(team);
    });

    const groupNames = ["A", "B", "C", "D", "E", "F"];
    let timeOffset = 0;

    // 2. Generate "All vs All" matches for each group
    groups.forEach((groupTeams, gIndex) => {
        const groupName = groupNames[gIndex] || `G${gIndex + 1}`;

        for (let i = 0; i < groupTeams.length; i++) {
            for (let j = i + 1; j < groupTeams.length; j++) {
                matches.push({
                    id: Math.random().toString(36).substr(2, 9),
                    tournamentId,
                    team1Id: groupTeams[i].id,
                    team2Id: groupTeams[j].id,
                    stage: 'GROUP',
                    group: groupName,
                    round: 1,
                    roundName: `Group ${groupName}`,
                    court: t.courts[matches.length % t.courts.length] || "Court 1",
                    scheduledTime: new Date(new Date().getTime() + (timeOffset * 60 * 60 * 1000)).toISOString(),
                    status: MatchStatus.SCHEDULED,
                    score: JSON.parse(JSON.stringify(initialScore))
                });
                timeOffset++;
            }
        }
    });

    // 3. Generate Playoff Placeholders (e.g., Semi Finals if we have enough groups/teams)
    // Simple logic: If we have at least 4 teams, assume Semis -> Final
    if (acceptedTeams.length >= 4) {
        // Semi 1
        matches.push({
            id: "semi_1",
            tournamentId,
            stage: 'PLAYOFF',
            round: 2,
            roundName: "Semi Final 1",
            court: "Center Court",
            scheduledTime: new Date(new Date().getTime() + ((timeOffset + 1) * 60 * 60 * 1000)).toISOString(),
            status: MatchStatus.SCHEDULED,
            score: JSON.parse(JSON.stringify(initialScore))
        });
        // Semi 2
        matches.push({
            id: "semi_2",
            tournamentId,
            stage: 'PLAYOFF',
            round: 2,
            roundName: "Semi Final 2",
            court: "Center Court",
            scheduledTime: new Date(new Date().getTime() + ((timeOffset + 2) * 60 * 60 * 1000)).toISOString(),
            status: MatchStatus.SCHEDULED,
            score: JSON.parse(JSON.stringify(initialScore))
        });
        // Final
        matches.push({
            id: "grand_final",
            tournamentId,
            stage: 'PLAYOFF',
            round: 3,
            roundName: "Grand Final",
            court: "Center Court",
            scheduledTime: new Date(new Date().getTime() + ((timeOffset + 4) * 60 * 60 * 1000)).toISOString(),
            status: MatchStatus.SCHEDULED,
            score: JSON.parse(JSON.stringify(initialScore))
        });
    }

  } else {
    // SINGLE ELIMINATION LOGIC
    // Shuffle teams for randomness
    const shuffled = [...acceptedTeams].sort(() => 0.5 - Math.random());

    // Create Bracket Structure
    let matchCount = 0;

    // Simple staggering for now (Round of X)
    // A proper bracket algo would need power of 2 padding, but let's stick to the requested simple pairing
    for (let i = 0; i < shuffled.length; i += 2) {
        if (i + 1 < shuffled.length) {
            matches.push({
                id: Math.random().toString(36).substr(2, 9),
                tournamentId,
                team1Id: shuffled[i].id,
                team2Id: shuffled[i + 1].id,
                stage: 'BRACKET',
                round: 1, 
                roundName: shuffled.length > 4 ? "Quarter Finals" : "Semi Finals",
                court: t.courts[matchCount % t.courts.length] || "Court 1",
                scheduledTime: new Date(new Date().getTime() + (matchCount * 60 * 60 * 1000)).toISOString(),
                status: MatchStatus.SCHEDULED,
                score: JSON.parse(JSON.stringify(initialScore))
            });
            matchCount++;
        }
    }

    // Placeholder Final
    matches.push({
        id: "final_match",
        tournamentId,
        team1Id: undefined,
        team2Id: undefined,
        stage: 'BRACKET',
        round: 2,
        roundName: "Grand Final",
        court: "Center Court",
        scheduledTime: new Date(new Date().getTime() + (24 * 60 * 60 * 1000)).toISOString(),
        status: MatchStatus.SCHEDULED,
        score: JSON.parse(JSON.stringify(initialScore))
    });
  }

  await updateDoc(tRef, { matches: matches });
};

export const updateMatchScore = async (tournamentId: string, matchId: string, newScore: ScoreState, status: MatchStatus, winnerId?: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;

  const t = tSnap.data() as Tournament;

  // 1. Update the Match
  const updatedMatches = t.matches.map(m => {
      if (m.id === matchId) {
          return {
              ...m,
              score: newScore,
              status: status,
              winnerTeamId: winnerId
          };
      }
      return m;
  });

  // 2. Update Team Standings (If Round Robin & Match Completed)
  let updatedTeams = [...t.teams];

  if (t.format === TournamentFormat.ROUND_ROBIN && status === MatchStatus.COMPLETED && winnerId) {
     // Recalculate stats from scratch based on all completed matches to ensure consistency
     // Reset stats first
     updatedTeams = updatedTeams.map(team => ({
         ...team,
         matchesPlayed: 0,
         wins: 0,
         losses: 0,
         points: 0,
         setsWon: 0,
         setsLost: 0
     }));

     // Iterate all COMPLETED matches
     updatedMatches.filter(m => m.status === MatchStatus.COMPLETED && m.stage === 'GROUP').forEach(match => {
         const winner = updatedTeams.find(tm => tm.id === match.winnerTeamId);
         const loser = updatedTeams.find(tm => tm.id === (match.winnerTeamId === match.team1Id ? match.team2Id : match.team1Id));

         if (winner && loser) {
             // Update Winner
             winner.matchesPlayed = (winner.matchesPlayed || 0) + 1;
             winner.wins = (winner.wins || 0) + 1;
             winner.points = (winner.points || 0) + 3; // 3 Points for a win
             // Approximate sets from score (assuming 2-0 or 2-1)
             // In Padel usually best of 3.
             // If winner won, they usually have 2 sets.
             const wSets = match.winnerTeamId === match.team1Id ? match.score.p1Sets : match.score.p2Sets;
             const lSets = match.winnerTeamId === match.team1Id ? match.score.p2Sets : match.score.p1Sets;

             winner.setsWon = (winner.setsWon || 0) + wSets;
             winner.setsLost = (winner.setsLost || 0) + lSets;

             // Update Loser
             loser.matchesPlayed = (loser.matchesPlayed || 0) + 1;
             loser.losses = (loser.losses || 0) + 1;
             loser.setsWon = (loser.setsWon || 0) + lSets;
             loser.setsLost = (loser.setsLost || 0) + wSets;
         }
     });
  }

  await updateDoc(tRef, { matches: updatedMatches, teams: updatedTeams });
};

export const addRefereeTag = async (tournamentId: string, matchId: string, tag: string) => {
    const tRef = doc(db, "tournaments", tournamentId);
    const tSnap = await getDoc(tRef);
    if (!tSnap.exists()) return;

    const t = tSnap.data() as Tournament;
    const updatedMatches = t.matches.map(m => {
        if (m.id === matchId) {
            const notes = m.refereeNotes || [];
            return { ...m, refereeNotes: [...notes, tag] };
        }
        return m;
    });

    await updateDoc(tRef, { matches: updatedMatches });
};

import React, { useState, useEffect } from 'react';
import { subscribeToTournaments, subscribeToTournament } from '../services/storage';
import { MatchStatus, Tournament, TournamentFormat } from '../types';
import { Clock, Trophy, ChevronRight, Grid } from 'lucide-react';

export const LiveScoreboard: React.FC = () => {
  const [tournaments, setTournaments] = useState<Tournament[]>([]);
  const [selectedTournamentId, setSelectedTournamentId] = useState<string | null>(null);
  const [activeTournament, setActiveTournament] = useState<Tournament | null>(null);

  // Listen to all tournaments
  useEffect(() => {
    const unsubscribe = subscribeToTournaments((data) => {
        setTournaments(data);
    });
    return () => unsubscribe();
  }, []);

  // Listen to selected tournament
  useEffect(() => {
    if (selectedTournamentId) {
        const unsubscribe = subscribeToTournament(selectedTournamentId, (data) => {
            setActiveTournament(data);
        });
        return () => unsubscribe();
    } else {
        setActiveTournament(null);
    }
  }, [selectedTournamentId]);

  if (!selectedTournamentId) {
      return (
          <div className="pb-20">
              <div className="text-center mb-8">
                <h1 className="text-3xl font-bold text-white mb-2">Live Scores</h1>
                <p className="text-gray-400">Select a tournament to view</p>
              </div>

              <div className="grid gap-4 max-w-2xl mx-auto">
                {tournaments.length === 0 && <p className="text-center text-gray-500">No active tournaments found.</p>}
                {tournaments.map(t => (
                    <button 
                        key={t.id}
                        onClick={() => setSelectedTournamentId(t.id)}
                        className="bg-[#0F213A] p-6 rounded-2xl border border-gray-800 flex justify-between items-center hover:border-[#E67E50] group transition-all text-left w-full"
                    >
                        <div>
                            <h3 className="text-xl font-bold text-white group-hover:text-[#E67E50] transition-colors">{t.name}</h3>
                            <div className="text-sm text-gray-400 mt-1 flex gap-4">
                                <span className="bg-gray-800 px-2 py-0.5 rounded text-xs">{t.format.replace('_', ' ')}</span>
                                <span>{t.teams.length} Teams</span>
                                <span>{t.matches.length} Matches</span>
                            </div>
                        </div>
                        <ChevronRight className="text-gray-600 group-hover:text-white" />
                    </button>
                ))}
              </div>
          </div>
      );
  }

  if (!activeTournament) return <div className="text-center p-10 text-gray-500">Loading tournament data...</div>;

  const liveMatches = activeTournament.matches.filter(m => m.status === MatchStatus.IN_PROGRESS);
  const upcomingMatches = activeTournament.matches.filter(m => m.status === MatchStatus.SCHEDULED).sort((a,b) => a.scheduledTime.localeCompare(b.scheduledTime));
  const completedMatches = activeTournament.matches.filter(m => m.status === MatchStatus.COMPLETED).reverse();

  return (
    <div className="space-y-8 md:space-y-12 pb-20">
        <button onClick={() => setSelectedTournamentId(null)} className="text-gray-500 hover:text-white mb-4 flex items-center gap-1 text-sm">
            &larr; Back to Tournaments
        </button>

        <div className="text-center mb-6 md:mb-8">
            <h1 className="text-2xl md:text-5xl font-bold text-white mb-2">{activeTournament.name}</h1>
            <p className="text-[#E67E50] text-sm md:text-base font-medium tracking-wide">LIVE TOURNAMENT FEED</p>
        </div>

      {/* LIVE SECTION */}
      <section>
        <div className="flex items-center gap-2 mb-4 md:mb-6">
            <span className="relative flex h-3 w-3">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                <span className="relative inline-flex rounded-full h-3 w-3 bg-red-500"></span>
            </span>
            <h2 className="text-lg md:text-xl font-bold text-white">Live Action</h2>
        </div>

        {liveMatches.length === 0 ? (
            <div className="p-8 bg-[#0F213A] rounded-2xl border border-gray-800 text-center text-gray-500 text-sm">
                No matches currently in progress.
            </div>
        ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 md:gap-6">
                {liveMatches.map(match => (
                    <LiveMatchCard key={match.id} match={match} teams={activeTournament.teams} />
                ))}
            </div>
        )}
      </section>

      {/* ROUND ROBIN STANDINGS */}
      {activeTournament.format === TournamentFormat.ROUND_ROBIN && (
          <section>
              <h2 className="text-lg md:text-xl font-bold text-white mb-4 md:mb-6 flex items-center gap-2"><Grid size={20}/> Group Standings</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                 {/* Logic to group teams by assignment would go here. For now, showing all in one table or inferred from match groups */}
                 <StandingsTable teams={activeTournament.teams} />
              </div>
          </section>
      )}

      {/* SCHEDULE */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 md:gap-12">
        <section>
             <h2 className="text-lg md:text-xl font-bold text-white mb-4 md:mb-6 flex items-center gap-2"><Clock size={20}/> Upcoming</h2>
             <div className="space-y-3">
                 {upcomingMatches.length === 0 && <p className="text-gray-500 text-sm">No upcoming matches.</p>}
                 {upcomingMatches.map(match => (
                     <MatchRow key={match.id} match={match} teams={activeTournament.teams} />
                 ))}
             </div>
        </section>

        <section>
             <h2 className="text-lg md:text-xl font-bold text-white mb-4 md:mb-6 text-gray-400">Completed</h2>
             <div className="space-y-3">
                 {completedMatches.length === 0 && <p className="text-gray-500 text-sm">No completed matches.</p>}
                 {completedMatches.map(match => (
                     <MatchRow key={match.id} match={match} teams={activeTournament.teams} />
                 ))}
             </div>
        </section>
      </div>
    </div>
  );
};

const StandingsTable = ({ teams }: any) => {
    // Sort by Points, then Wins, then Set Difference (Sets Won - Sets Lost)
    const sortedTeams = [...teams].sort((a, b) => {
        if ((b.points || 0) !== (a.points || 0)) return (b.points || 0) - (a.points || 0);
        if ((b.wins || 0) !== (a.wins || 0)) return (b.wins || 0) - (a.wins || 0);
        return ((b.setsWon || 0) - (b.setsLost || 0)) - ((a.setsWon || 0) - (a.setsLost || 0));
    });

    return (
        <div className="bg-[#0F213A] rounded-xl border border-gray-800 overflow-hidden">
            <div className="overflow-x-auto">
                <table className="w-full text-sm text-left">
                    <thead className="text-xs text-gray-400 uppercase bg-[#0A1628]">
                        <tr>
                            <th className="px-4 py-3">Team</th>
                            <th className="px-4 py-3 text-center">MP</th>
                            <th className="px-4 py-3 text-center">W</th>
                            <th className="px-4 py-3 text-center">L</th>
                            <th className="px-4 py-3 text-center">Sets</th>
                            <th className="px-4 py-3 text-center text-[#E67E50]">Pts</th>
                        </tr>
                    </thead>
                    <tbody>
                        {sortedTeams.map((team, index) => (
                            <tr key={team.id} className="border-b border-gray-800 last:border-0 hover:bg-[#132640]">
                                <td className="px-4 py-3 font-medium text-white flex items-center gap-2">
                                    <span className="text-gray-500 text-xs w-4">{index + 1}.</span>
                                    <div className="truncate max-w-[120px]">{team.name}</div>
                                </td>
                                <td className="px-4 py-3 text-center text-gray-400">{team.matchesPlayed || 0}</td>
                                <td className="px-4 py-3 text-center text-green-500">{team.wins || 0}</td>
                                <td className="px-4 py-3 text-center text-red-500">{team.losses || 0}</td>
                                <td className="px-4 py-3 text-center text-gray-400 text-xs">
                                    {team.setsWon || 0}-{team.setsLost || 0}
                                </td>
                                <td className="px-4 py-3 text-center font-bold text-[#E67E50]">{team.points || 0}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

const LiveMatchCard = ({ match, teams }: any) => {
    const t1 = teams.find((t: any) => t.id === match.team1Id);
    const t2 = teams.find((t: any) => t.id === match.team2Id);

    return (
        <div className="bg-gradient-to-br from-[#0F213A] to-[#162a45] rounded-2xl p-4 md:p-6 border border-[#E67E50] shadow-lg shadow-orange-900/10 relative overflow-hidden">
             {/* Background Element */}
            <div className="absolute -right-10 -top-10 w-40 h-40 bg-[#E67E50]/5 rounded-full blur-3xl"></div>

            <div className="flex justify-between items-start mb-4 md:mb-6">
                 <span className="bg-red-500 text-white text-[10px] font-bold px-2 py-1 rounded animate-pulse">LIVE</span>
                 <div className="text-right">
                     <div className="text-xs md:text-sm text-gray-400 font-medium">{match.court}</div>
                     {match.group && <div className="text-[10px] text-[#E67E50] font-bold">GROUP {match.group}</div>}
                 </div>
            </div>

            <div className="flex items-center justify-between gap-2 md:gap-4">
                <div className="flex-1 min-w-0">
                    <div className="text-lg md:text-2xl font-bold text-white leading-tight truncate">{t1.name}</div>
                </div>
                <div className="text-center px-2 md:px-4 shrink-0">
                    <div className="text-3xl md:text-4xl font-mono font-bold text-[#E67E50] tracking-tighter">
                        {match.score.p1Sets}-{match.score.p2Sets}
                    </div>
                    <div className="text-[10px] md:text-xs text-gray-500 uppercase mt-1">Sets</div>
                </div>
                <div className="flex-1 text-right min-w-0">
                    <div className="text-lg md:text-2xl font-bold text-white leading-tight truncate">{t2.name}</div>
                </div>
            </div>

            {/* Detailed Score */}
            <div className="mt-4 md:mt-6 pt-4 border-t border-gray-700/50 flex justify-center gap-6 md:gap-8 text-xs md:text-sm">
                 <div className="text-center">
                    <div className="text-gray-500 mb-1">Games</div>
                    <div className="font-mono text-white text-base md:text-lg">{match.score.p1Games} - {match.score.p2Games}</div>
                 </div>
                 <div className="text-center">
                    <div className="text-gray-500 mb-1">Points</div>
                    <div className="font-mono text-[#E67E50] font-bold text-base md:text-lg">{match.score.p1Points} - {match.score.p2Points}</div>
                 </div>
            </div>
        </div>
    )
}

const MatchRow = ({ match, teams }: any) => {
    const t1 = teams.find((t: any) => t.id === match.team1Id);
    const t2 = teams.find((t: any) => t.id === match.team2Id);

    // If teams are not decided yet (bracket placeholders)
    if (!t1 || !t2) return (
         <div className="bg-[#0F213A] p-3 md:p-4 rounded-xl border border-gray-800 flex justify-between items-center opacity-50">
            <div className="flex flex-col">
                <span className="text-gray-500 italic text-sm">{match.roundName}</span>
                {match.stage === 'PLAYOFF' && <span className="text-[10px] text-[#E67E50]">PLAYOFF</span>}
            </div>
            <span className="text-xs text-gray-600">TBD vs TBD</span>
        </div>
    );

    return (
        <div className="bg-[#0F213A] p-3 md:p-4 rounded-xl border border-gray-800 hover:bg-[#132640] transition-colors relative overflow-hidden">
            {match.group && <div className="absolute top-0 right-0 bg-gray-800 text-[9px] text-gray-400 px-2 py-0.5 rounded-bl-lg">GRP {match.group}</div>}

            <div className="flex justify-between items-center gap-3">
                <div className="flex-1 min-w-0">
                     <div className="flex justify-between items-center mb-1">
                        <div className={`font-bold truncate text-sm md:text-base pr-2 ${match.winnerTeamId === t1.id ? 'text-[#E67E50]' : 'text-white'}`}>{t1.name}</div>
                        <div className="font-mono text-gray-400 text-sm">{match.score.p1Sets}</div>
                     </div>
                     <div className="flex justify-between items-center">
                        <div className={`font-bold truncate text-sm md:text-base pr-2 ${match.winnerTeamId === t2.id ? 'text-[#E67E50]' : 'text-white'}`}>{t2.name}</div>
                        <div className="font-mono text-gray-400 text-sm">{match.score.p2Sets}</div>
                     </div>
                </div>
                <div className="ml-2 pl-2 md:ml-4 md:pl-4 border-l border-gray-700 text-[10px] md:text-xs text-gray-500 w-16 md:w-24 text-right shrink-0">
                    {match.status === MatchStatus.COMPLETED ? 'Final' : new Date(match.scheduledTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                </div>
            </div>
            {match.refereeNotes && match.refereeNotes.length > 0 && (
                <div className="flex flex-wrap gap-1.5 mt-2 md:mt-3 pt-2 border-t border-gray-700/30">
                    {match.refereeNotes.map((tag: string, i: number) => (
                        <span key={i} className="text-[10px] font-medium text-[#E67E50] bg-[#E67E50]/10 px-2 py-0.5 rounded border border-[#E67E50]/20 uppercase tracking-wide">
                            {tag}
                        </span>
                    ))}
                </div>
            )}
        </div>
    )
}

import React, { useState, useEffect } from 'react';
import { TournamentFormat, SkillLevel, Tournament, RegistrationStatus } from '../types';
import { createTournament, subscribeToTournaments, subscribeToTournament, updateTeamStatus, generateSchedule } from '../services/storage';
import { Check, X, Calendar, Users, Trophy, PlayCircle, Lock, RefreshCcw, ChevronLeft, Plus, ChevronRight } from 'lucide-react';

export const AdminDashboard: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');

  const [tournaments, setTournaments] = useState<Tournament[]>([]);
  const [selectedTournamentId, setSelectedTournamentId] = useState<string | null>(null);
  const [activeTournament, setActiveTournament] = useState<Tournament | null>(null);

  const [isCreating, setIsCreating] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    format: TournamentFormat.SINGLE_ELIMINATION,
    skillLevel: SkillLevel.INTERMEDIATE,
    maxTeams: 8,
    entryFee: 100,
    prizeMoney: 1000,
    refereePasscode: '1234',
    courts: 'Court 1, Court 2',
    registrationDeadline: ''
  });

  // Listen to tournaments list
  useEffect(() => {
    if (isAuthenticated) {
        const unsubscribe = subscribeToTournaments((data) => setTournaments(data));
        return () => unsubscribe();
    }
  }, [isAuthenticated]);

  // Listen to active tournament details
  useEffect(() => {
    if (selectedTournamentId) {
        const unsubscribe = subscribeToTournament(selectedTournamentId, (data) => setActiveTournament(data));
        return () => unsubscribe();
    } else {
        setActiveTournament(null);
    }
  }, [selectedTournamentId]);

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (loginEmail === 'zayam@test.com' && loginPassword === 'Test123!@#') {
        setIsAuthenticated(true);
    } else {
        alert("Invalid credentials. Please use the testing account.");
    }
  };

  const handleCreate = async () => {
    if (!formData.name) return alert("Name is required");
    try {
        const id = await createTournament({
            name: formData.name,
            format: formData.format,
            skillLevel: formData.skillLevel,
            maxTeams: formData.maxTeams,
            entryFee: formData.entryFee,
            prizeMoney: formData.prizeMoney,
            refereePasscode: formData.refereePasscode,
            courts: formData.courts.split(',').map(s => s.trim()),
            registrationDeadline: formData.registrationDeadline || new Date().toISOString()
        });
        setIsCreating(false);
        setSelectedTournamentId(id);
    } catch (e) {
        alert("Failed to create tournament. Check console.");
    }
  };

  const handleTeamAction = async (teamId: string, status: 'ACCEPTED' | 'REJECTED') => {
    if (!activeTournament) return;
    await updateTeamStatus(activeTournament.id, teamId, status);
  };

  const handleGenerateSchedule = async () => {
    if (!activeTournament) return;

    if (activeTournament.matches.length > 0) {
      const confirmReset = window.confirm(
        "Warning: This will delete all current matches, scores, and referee tags. Are you sure you want to regenerate the bracket?"
      );
      if (!confirmReset) return;
    }

    await generateSchedule(activeTournament.id);
    alert("Bracket generated successfully!");
  }

  if (!isAuthenticated) {
    return (
        <div className="max-w-md mx-auto mt-10 md:mt-20 p-6 md:p-8 bg-[#0F213A] rounded-2xl border border-gray-800 shadow-2xl mx-4">
            <div className="flex justify-center mb-6">
                <div className="h-16 w-16 bg-[#E67E50]/20 rounded-full flex items-center justify-center text-[#E67E50] shadow-[0_0_15px_rgba(230,126,80,0.3)]">
                    <Lock size={32} />
                </div>
            </div>
            <h2 className="text-2xl font-bold text-center text-white mb-2">Admin Portal</h2>
            <p className="text-gray-400 text-center text-sm mb-8">Please sign in to manage tournaments</p>

            <form onSubmit={handleLogin} className="space-y-4">
                <div>
                    <label className="block text-xs font-medium text-gray-400 mb-1 uppercase tracking-wider">Email Address</label>
                    <input 
                        type="email"
                        className="w-full bg-[#0A1628] border border-gray-700 rounded-xl p-3 text-white focus:border-[#E67E50] focus:ring-1 focus:ring-[#E67E50] focus:outline-none transition-all"
                        value={loginEmail}
                        onChange={e => setLoginEmail(e.target.value)}
                        placeholder="admin@matchup.com"
                    />
                </div>
                <div>
                    <label className="block text-xs font-medium text-gray-400 mb-1 uppercase tracking-wider">Password</label>
                    <input 
                        type="password"
                        className="w-full bg-[#0A1628] border border-gray-700 rounded-xl p-3 text-white focus:border-[#E67E50] focus:ring-1 focus:ring-[#E67E50] focus:outline-none transition-all"
                        value={loginPassword}
                        onChange={e => setLoginPassword(e.target.value)}
                        placeholder="••••••••"
                    />
                </div>
                <button className="w-full bg-[#E67E50] hover:bg-[#ff8f5d] text-white font-bold py-4 rounded-xl transition-all shadow-lg shadow-orange-500/20 mt-4">
                    Sign In
