  }

  if (!activeTournament) return <div className="p-8 text-center text-gray-500">Loading...</div>;

  // View: Tournament Detail (Dashboard)
  const pendingTeams = activeTournament.teams.filter(t => t.status === RegistrationStatus.PENDING);
  const acceptedTeams = activeTournament.teams.filter(t => t.status === RegistrationStatus.ACCEPTED);

  return (
    <div className="space-y-6 md:space-y-8 pb-20">
      {/* Header */}
