// ------------------------------------------------------------------
// SHARED LOGIC (Schedule Generation & Stats)
// ------------------------------------------------------------------

const calculateSchedule = (t: Tournament): Match[] => {
  const acceptedTeams = t.teams.filter(tm => tm.status === RegistrationStatus.ACCEPTED);
  if (acceptedTeams.length < 2) return [];

  let matches: Match[] = [];

  if (t.format === TournamentFormat.ROUND_ROBIN) {
    // ROUND ROBIN LOGIC
    const teamsPerGroup = 4;
    const numGroups = Math.ceil(acceptedTeams.length / teamsPerGroup);
    const groups: Team[][] = Array.from({ length: numGroups }, () => []);

    // Distribute teams
    acceptedTeams.forEach((team, index) => {
        groups[index % numGroups].push(team);
    });

    const groupNames = ["A", "B", "C", "D", "E", "F"];
    let timeOffset = 0;

    // Group Matches
    groups.forEach((groupTeams, gIndex) => {
        const groupName = groupNames[gIndex] || `G${gIndex + 1}`;
        for (let i = 0; i < groupTeams.length; i++) {
            for (let j = i + 1; j < groupTeams.length; j++) {
                matches.push({
                    id: Math.random().toString(36).substr(2, 9),
                    tournamentId: t.id,
                    team1Id: groupTeams[i].id,
                    team2Id: groupTeams[j].id,
                    stage: 'GROUP',
                    group: groupName,
                    round: 1,
                    roundName: `Group ${groupName}`,
                    court: t.courts[matches.length % t.courts.length] || "Court 1",
                    scheduledTime: new Date(new Date().getTime() + (timeOffset * 60 * 60 * 1000)).toISOString(),
                    status: MatchStatus.SCHEDULED,
                    score: JSON.parse(JSON.stringify(initialScore))
                });
                timeOffset++;
            }
        }
    });

    // Playoff Placeholders
    if (acceptedTeams.length >= 4) {
        matches.push({
            id: "semi_1", tournamentId: t.id, stage: 'PLAYOFF', round: 2, roundName: "Semi Final 1", court: "Center Court",
            scheduledTime: new Date(new Date().getTime() + ((timeOffset + 1) * 60 * 60 * 1000)).toISOString(),
            status: MatchStatus.SCHEDULED, score: JSON.parse(JSON.stringify(initialScore))
        });
        matches.push({
            id: "semi_2", tournamentId: t.id, stage: 'PLAYOFF', round: 2, roundName: "Semi Final 2", court: "Center Court",
            scheduledTime: new Date(new Date().getTime() + ((timeOffset + 2) * 60 * 60 * 1000)).toISOString(),
            status: MatchStatus.SCHEDULED, score: JSON.parse(JSON.stringify(initialScore))
        });
        matches.push({
            id: "grand_final", tournamentId: t.id, stage: 'PLAYOFF', round: 3, roundName: "Grand Final", court: "Center Court",
            scheduledTime: new Date(new Date().getTime() + ((timeOffset + 4) * 60 * 60 * 1000)).toISOString(),
            status: MatchStatus.SCHEDULED, score: JSON.parse(JSON.stringify(initialScore))
        });
    }

  } else {
    // SINGLE ELIMINATION
    const shuffled = [...acceptedTeams].sort(() => 0.5 - Math.random());
    let matchCount = 0;

    for (let i = 0; i < shuffled.length; i += 2) {
        if (i + 1 < shuffled.length) {
            matches.push({
                id: Math.random().toString(36).substr(2, 9),
                tournamentId: t.id,
                team1Id: shuffled[i].id,
                team2Id: shuffled[i + 1].id,
                stage: 'BRACKET',
                round: 1, 
                roundName: shuffled.length > 4 ? "Quarter Finals" : "Semi Finals",
                court: t.courts[matchCount % t.courts.length] || "Court 1",
                scheduledTime: new Date(new Date().getTime() + (matchCount * 60 * 60 * 1000)).toISOString(),
                status: MatchStatus.SCHEDULED,
                score: JSON.parse(JSON.stringify(initialScore))
            });
            matchCount++;
        }
    }
    matches.push({
        id: "final_match", tournamentId: t.id, team1Id: undefined, team2Id: undefined, stage: 'BRACKET', round: 2, roundName: "Grand Final", court: "Center Court",
        scheduledTime: new Date(new Date().getTime() + (24 * 60 * 60 * 1000)).toISOString(),
        status: MatchStatus.SCHEDULED, score: JSON.parse(JSON.stringify(initialScore))
    });
  }
  return matches;
};

const calculateStats = (teams: Team[], matches: Match[], format: TournamentFormat): Team[] => {
    if (format !== TournamentFormat.ROUND_ROBIN) return teams;

    // Reset stats
    let updatedTeams = teams.map(team => ({
        ...team,
        matchesPlayed: 0,
        wins: 0,
        losses: 0,
        points: 0,
        setsWon: 0,
        setsLost: 0
    }));

    matches.filter(m => m.status === MatchStatus.COMPLETED && m.stage === 'GROUP').forEach(match => {
         const winner = updatedTeams.find(tm => tm.id === match.winnerTeamId);
         const loser = updatedTeams.find(tm => tm.id === (match.winnerTeamId === match.team1Id ? match.team2Id : match.team1Id));

         if (winner && loser) {
             // Winner Stats
             winner.matchesPlayed = (winner.matchesPlayed || 0) + 1;
             winner.wins = (winner.wins || 0) + 1;
             winner.points = (winner.points || 0) + 3;

             const wSets = match.winnerTeamId === match.team1Id ? match.score.p1Sets : match.score.p2Sets;
             const lSets = match.winnerTeamId === match.team1Id ? match.score.p2Sets : match.score.p1Sets;

             winner.setsWon = (winner.setsWon || 0) + wSets;
             winner.setsLost = (winner.setsLost || 0) + lSets;

             // Loser Stats
             loser.matchesPlayed = (loser.matchesPlayed || 0) + 1;
             loser.losses = (loser.losses || 0) + 1;
             loser.setsWon = (loser.setsWon || 0) + lSets;
             loser.setsLost = (loser.setsLost || 0) + wSets;
         }
    });

    return updatedTeams;
};

