const fb_subscribeToTournaments = (callback: (tournaments: Tournament[]) => void) => {
  const q = query(collection(db, "tournaments"), orderBy("createdAt", "desc"));
  return onSnapshot(q, (snapshot) => {
    const tournaments = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })) as Tournament[];
    callback(tournaments);
  });
};

const fb_subscribeToTournament = (id: string, callback: (t: Tournament | null) => void) => {
  return onSnapshot(doc(db, "tournaments", id), (doc) => {
    if (doc.exists()) callback({ id: doc.id, ...doc.data() } as Tournament);
    else callback(null);
  });
};

const fb_createTournament = async (t: any) => {
  const newTournament = { ...t, teams: [], matches: [], status: 'ACTIVE', createdAt: new Date().toISOString() };
  const docRef = await addDoc(collection(db, "tournaments"), newTournament);
  return docRef.id;
};

const fb_registerTeam = async (tournamentId: string, team: any) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) throw new Error("Tournament not found");

  const currentData = tSnap.data() as Tournament;
  const newTeam = { ...team, id: Math.random().toString(36).substr(2, 9), status: RegistrationStatus.PENDING, registeredAt: new Date().toISOString(), wins:0, losses:0, points:0, setsWon:0, setsLost:0, matchesPlayed:0 };
  await updateDoc(tRef, { teams: [...currentData.teams, newTeam] });
};

const fb_updateTeamStatus = async (tournamentId: string, teamId: string, status: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;
  const t = tSnap.data() as Tournament;
  const updatedTeams = t.teams.map(tm => tm.id === teamId ? { ...tm, status: status as RegistrationStatus } : tm);
  await updateDoc(tRef, { teams: updatedTeams });
};

const fb_generateSchedule = async (tournamentId: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;

  const matches = calculateSchedule({ id: tSnap.id, ...tSnap.data() } as Tournament);
  await updateDoc(tRef, { matches: matches });
};

const fb_updateMatchScore = async (tournamentId: string, matchId: string, newScore: ScoreState, status: MatchStatus, winnerId?: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;

  const t = tSnap.data() as Tournament;
  const updatedMatches = t.matches.map(m => m.id === matchId ? { ...m, score: newScore, status, winnerTeamId: winnerId } : m);
  const updatedTeams = calculateStats(t.teams, updatedMatches, t.format);

  await updateDoc(tRef, { matches: updatedMatches, teams: updatedTeams });
};

const fb_addRefereeTag = async (tournamentId: string, matchId: string, tag: string) => {
  const tRef = doc(db, "tournaments", tournamentId);
  const tSnap = await getDoc(tRef);
  if (!tSnap.exists()) return;
  const t = tSnap.data() as Tournament;
  const updatedMatches = t.matches.map(m => m.id === matchId ? { ...m, refereeNotes: [...(m.refereeNotes || []), tag] } : m);
  await updateDoc(tRef, { matches: updatedMatches });
};


// ------------------------------------------------------------------
// LOCAL STORAGE IMPLEMENTATION (Fallback)
// ------------------------------------------------------------------
const LS_KEY = "matchup_local_db";

const getLS = (): Tournament[] => {
    const data = localStorage.getItem(LS_KEY);
    return data ? JSON.parse(data) : [];
}

const saveLS = (data: Tournament[]) => {
    localStorage.setItem(LS_KEY, JSON.stringify(data));
    window.dispatchEvent(new Event('storage_update'));
}

const ls_subscribeToTournaments = (callback: (tournaments: Tournament[]) => void) => {
    const notify = () => callback(getLS().sort((a,b) => b.registrationDeadline.localeCompare(a.registrationDeadline))); // Sort desc roughly
    notify();
    const handler = () => notify();
    window.addEventListener('storage_update', handler);
    // Polling for good measure in case of cross-tab issues in some browsers
    const interval = setInterval(notify, 1000); 
    return () => { window.removeEventListener('storage_update', handler); clearInterval(interval); }
};

const ls_subscribeToTournament = (id: string, callback: (t: Tournament | null) => void) => {
    const notify = () => {
        const t = getLS().find(x => x.id === id);
        callback(t || null);
    }
    notify();
    const handler = () => notify();
    window.addEventListener('storage_update', handler);
    const interval = setInterval(notify, 1000); 
    return () => { window.removeEventListener('storage_update', handler); clearInterval(interval); }
};

const ls_createTournament = async (t: any) => {
    const db = getLS();
    const newT = { id: Math.random().toString(36).substr(2, 9), ...t, teams: [], matches: [], status: 'ACTIVE', createdAt: new Date().toISOString() };
    db.push(newT);
    saveLS(db);
    return newT.id;
};

const ls_registerTeam = async (tournamentId: string, team: any) => {
    const db = getLS();
    const idx = db.findIndex(t => t.id === tournamentId);
    if (idx === -1) throw new Error("Not found");
    const newTeam = { ...team, id: Math.random().toString(36).substr(2, 9), status: RegistrationStatus.PENDING, registeredAt: new Date().toISOString(), wins:0, losses:0, points:0, setsWon:0, setsLost:0, matchesPlayed:0 };
    db[idx].teams.push(newTeam);
    saveLS(db);
};

const ls_updateTeamStatus = async (tournamentId: string, teamId: string, status: string) => {
    const db = getLS();
    const idx = db.findIndex(t => t.id === tournamentId);
    if (idx === -1) return;
    db[idx].teams = db[idx].teams.map(tm => tm.id === teamId ? { ...tm, status: status as RegistrationStatus } : tm);
    saveLS(db);
};

const ls_generateSchedule = async (tournamentId: string) => {
    const db = getLS();
    const idx = db.findIndex(t => t.id === tournamentId);
    if (idx === -1) return;

    db[idx].matches = calculateSchedule(db[idx]);
    saveLS(db);
};

const ls_updateMatchScore = async (tournamentId: string, matchId: string, newScore: ScoreState, status: MatchStatus, winnerId?: string) => {
    const db = getLS();
    const idx = db.findIndex(t => t.id === tournamentId);
    if (idx === -1) return;

    db[idx].matches = db[idx].matches.map(m => m.id === matchId ? { ...m, score: newScore, status, winnerTeamId: winnerId } : m);
    // Recalculate stats
    db[idx].teams = calculateStats(db[idx].teams, db[idx].matches, db[idx].format);

    saveLS(db);
};

const ls_addRefereeTag = async (tournamentId: string, matchId: string, tag: string) => {
    const db = getLS();
    const idx = db.findIndex(t => t.id === tournamentId);
    if (idx === -1) return;
    db[idx].matches = db[idx].matches.map(m => m.id === matchId ? { ...m, refereeNotes: [...(m.refereeNotes || []), tag] } : m);
    saveLS(db);
};


