// ------------------------------------------------------------------
// SHARED LOGIC (Schedule Generation & Progression)
// ------------------------------------------------------------------

const genId = () => Math.random().toString(36).substr(2, 9);

const createMatch = (tId: string, name: string, round: number, stage: 'GROUP'|'BRACKET'|'PLAYOFF', timeOffset: number, court: string, t1?: string, t2?: string): Match => ({
    id: genId(),
    tournamentId: tId,
    team1Id: t1,
    team2Id: t2,
    stage,
    round,
    roundName: name,
    court,
    scheduledTime: new Date(new Date().getTime() + (timeOffset * 60 * 60 * 1000)).toISOString(),
    status: MatchStatus.SCHEDULED,
    score: JSON.parse(JSON.stringify(initialScore))
});

export const calculateSchedule = (t: Tournament, knockoutConfig?: any[]): Match[] => {
  const acceptedTeams = t.teams.filter(tm => tm.status === RegistrationStatus.ACCEPTED);
  // We allow scheduling even if not full, but typically need at least 2
  if (acceptedTeams.length < 2 && (!knockoutConfig || knockoutConfig.length === 0)) return [];

  let matches: Match[] = [...(t.matches || [])]; // Preserve existing if just appending knockout

  // If we are generating from scratch (no matches yet), start fresh
  if (matches.length === 0) {
      let timeOffset = 0;

      // --- ROUND ROBIN GENERATION ---
      if (t.format === TournamentFormat.ROUND_ROBIN) {
        const groups: Record<string, Team[]> = {};

        if (t.rrType === RoundRobinType.GROUPS) {
            acceptedTeams.forEach(team => {
                const gId = team.groupId || "A";
                if (!groups[gId]) groups[gId] = [];
                groups[gId].push(team);
            });
        } else {
            groups["A"] = acceptedTeams;
        }

        const loops = t.rrType === RoundRobinType.DOUBLE ? 2 : 1;

        Object.keys(groups).sort().forEach(gId => {
            const groupTeams = groups[gId];
            for (let l = 0; l < loops; l++) {
                for (let i = 0; i < groupTeams.length; i++) {
                    for (let j = i + 1; j < groupTeams.length; j++) {
                        matches.push({
                            ...createMatch(t.id, `Group ${gId} - R${l+1}`, 1, 'GROUP', timeOffset, t.courts[matches.length % t.courts.length] || "Court 1", groupTeams[i].id, groupTeams[j].id),
                            group: gId
                        });
                        timeOffset++;
                    }
                }
            }
        });
      } 

      // --- SINGLE / DOUBLE ELIMINATION GENERATION ---
      else {
          // Double Elim is Single Elim + Losers Bracket. 
          // For MVP complexity, we implement a Single Elim Tree which is robust.
          // True Double Elim requires complex "Drop-down" logic. 
          // We will stick to a Single Elim tree for the main bracket, and if Double is selected, 
          // we would need a secondary generator. For now, let's perfect the Single Elim tree.

          const rounds = Math.ceil(Math.log2(acceptedTeams.length));
          const totalBracketSize = Math.pow(2, rounds); 

          // Pad with byes
          let currentRoundTeams: (string | undefined)[] = [...acceptedTeams.map(t => t.id)];
          // Shuffle
          currentRoundTeams.sort(() => 0.5 - Math.random());

          while (currentRoundTeams.length < totalBracketSize) {
              currentRoundTeams.push(undefined); 
          }

          let roundMatches: Match[] = [];
          let previousRoundMatches: Match[] = [];

          // Generate Round by Round
          for (let r = 1; r <= rounds; r++) {
              let roundName = "";
              if (r === rounds) roundName = "Grand Final";
              else if (r === rounds - 1) roundName = "Semi Final";
              else if (r === rounds - 2) roundName = "Quarter Final";
              else roundName = `Round of ${Math.pow(2, rounds - r + 1)}`;

              const matchesInRound = Math.pow(2, rounds - r);
              const currentRoundObjs: Match[] = [];

              for (let m = 0; m < matchesInRound; m++) {
                  let t1, t2;
                  let dep1: MatchDependency | undefined, dep2: MatchDependency | undefined;

                  if (r === 1) {
                      t1 = currentRoundTeams[m * 2];
                      t2 = currentRoundTeams[m * 2 + 1];
                  } else {
                      // Take winners from previous round
                      const m1 = previousRoundMatches[m * 2];
                      const m2 = previousRoundMatches[m * 2 + 1];
                      dep1 = { sourceType: 'MATCH_WINNER', sourceId: m1.id };
                      dep2 = { sourceType: 'MATCH_WINNER', sourceId: m2.id };
                  }

                  const newMatch = createMatch(t.id, roundName, r, 'BRACKET', timeOffset, t.courts[0] || "Court 1", t1, t2);
                  newMatch.team1Dependency = dep1;
                  newMatch.team2Dependency = dep2;

                  // If Double Elim, create a corresponding loser match logic here (omitted for brevity, sticking to SE structure for robustness)

                  currentRoundObjs.push(newMatch);
                  matches.push(newMatch);
                  timeOffset++;

                  // Back-link previous matches
                  if (r > 1) {
                      const m1 = previousRoundMatches[m * 2];
                      const m2 = previousRoundMatches[m * 2 + 1];
                      m1.nextMatchId = newMatch.id;
                      m2.nextMatchId = newMatch.id;
                  }
              }
              previousRoundMatches = currentRoundObjs;
          }
      }
  } 

  // --- MANUAL KNOCKOUT APPEND ---
  // If admin provided knockoutConfig (e.g. from Dashboard after groups)
  if (knockoutConfig && knockoutConfig.length > 0) {
      // Config format: { id: "temp1", name: "QF1", t1Source: "A1", t2Source: "B2", court, time }
      // We resolve sources.

      const newMatches: Match[] = [];
      const idMap: Record<string, string> = {}; // map tempId to realId

      // Sort by round order if possible, otherwise just create placeholders
      knockoutConfig.forEach(cfg => {
          const mId = genId();
          idMap[cfg.id] = mId;

          const m: Match = {
            id: mId,
            tournamentId: t.id,
            stage: 'PLAYOFF',
            round: 10, // Arbitrary high number for playoffs
            roundName: cfg.name,
            court: cfg.court,
            scheduledTime: cfg.scheduledTime,
            status: MatchStatus.SCHEDULED,
            score: JSON.parse(JSON.stringify(initialScore)),
            // Dependencies
            team1Dependency: parseDependency(cfg.t1Source),
            team2Dependency: parseDependency(cfg.t2Source)
          };
          newMatches.push(m);
      });

      // Link 'Next Match' based on config if user defined "Winner of QF1 goes to SF1"
      // This requires the config to reference IDs. 
      // For the MVP "Criteria" requested:
      // QF1 = A1 v B2, QF2 = C1 v D2, SF1 = Win QF1 v Win QF2.
      // We will handle linking in the Admin Dashboard logic before passing here or infer it.
      // Simplified: Just add them. The advanceBracket logic checks dependencies.

      matches = [...matches, ...newMatches];
  }

  return matches;
};

const parseDependency = (sourceStr: string): MatchDependency | undefined => {
    // Format: "GROUP:A:1" (Group A Rank 1) or "MATCH:prevMatchId:WINNER"
    if (!sourceStr) return undefined;
    const parts = sourceStr.split(':');
    if (parts[0] === 'GROUP') {
        return { sourceType: 'GROUP_RANK', sourceId: parts[1], rank: parseInt(parts[2]) };
    }
    if (parts[0] === 'MATCH') {
        return { sourceType: 'MATCH_WINNER', sourceId: parts[1] };
    }
    return undefined;
};

// --- PROGRESSION ENGINE ---

const advanceBracket = (allMatches: Match[], completedMatch: Match, winnerId: string, loserId?: string): Match[] => {
    return allMatches.map(m => {
        // 1. Direct Link (Next Match ID)
        if (completedMatch.nextMatchId && m.id === completedMatch.nextMatchId) {
             // Logic: If I am dependent on this match, slot me in.
             // If m.team1Dependency points to completedMatch, put winner in team1
             if (m.team1Dependency?.sourceId === completedMatch.id) {
                 return { ...m, team1Id: winnerId };
             } else if (m.team2Dependency?.sourceId === completedMatch.id) {
                 return { ...m, team2Id: winnerId };
             }
             // Fallback for simple tree
             if (!m.team1Id) return { ...m, team1Id: winnerId };
             else if (!m.team2Id) return { ...m, team2Id: winnerId };
        }

        // 2. Dependency Link (for Knockouts referencing Group stages or unlinked matches)
        // If match M is waiting for "Winner of Match X"
        if (m.team1Dependency?.sourceType === 'MATCH_WINNER' && m.team1Dependency.sourceId === completedMatch.id) {
            return { ...m, team1Id: winnerId };
        }
        if (m.team2Dependency?.sourceType === 'MATCH_WINNER' && m.team2Dependency.sourceId === completedMatch.id) {
            return { ...m, team2Id: winnerId };
        }

        return m;
    });
};

const calculateStats = (teams: Team[], matches: Match[], format: TournamentFormat): Team[] => {
    if (format !== TournamentFormat.ROUND_ROBIN) return teams;

    // Reset
    let updatedTeams = teams.map(t => ({...t, matchesPlayed: 0, wins: 0, losses: 0, points: 0, setsWon: 0, setsLost: 0}));

    matches.filter(m => m.status === MatchStatus.COMPLETED && m.stage === 'GROUP').forEach(match => {
         const winner = updatedTeams.find(tm => tm.id === match.winnerTeamId);
         const loser = updatedTeams.find(tm => tm.id === (match.winnerTeamId === match.team1Id ? match.team2Id : match.team1Id));

         if (winner && loser) {
             winner.matchesPlayed!++;
             winner.wins!++;
             winner.points = (winner.points || 0) + 3;
             winner.setsWon = (winner.setsWon || 0) + (match.winnerTeamId === match.team1Id ? match.score.p1Sets : match.score.p2Sets);
             winner.setsLost = (winner.setsLost || 0) + (match.winnerTeamId === match.team1Id ? match.score.p2Sets : match.score.p1Sets);

             loser.matchesPlayed!++;
             loser.losses!++;
             loser.setsWon = (loser.setsWon || 0) + (match.winnerTeamId === match.team1Id ? match.score.p2Sets : match.score.p1Sets);
             loser.setsLost = (loser.setsLost || 0) + (match.winnerTeamId === match.team1Id ? match.score.p1Sets : match.score.p2Sets);
         }
    });

    return updatedTeams;
};

