// EXPORTS (STORAGE ADAPTER)
// ------------------------------------------------------------------
// Using LocalStorage for reliability if Firebase not configured
const LS_KEY = "matchup_local_db";
const getLS = (): Tournament[] => JSON.parse(localStorage.getItem(LS_KEY) || "[]");
const saveLS = (data: Tournament[]) => {
    localStorage.setItem(LS_KEY, JSON.stringify(data));
    window.dispatchEvent(new Event('storage_update'));
}

export const subscribeToTournaments = (cb: any) => {
    const n = () => cb(getLS());
    window.addEventListener('storage_update', n);
    n();
    return () => window.removeEventListener('storage_update', n);
};

export const subscribeToTournament = (id: string, cb: any) => {
    const n = () => cb(getLS().find(t => t.id === id) || null);
    window.addEventListener('storage_update', n);
    n();
    return () => window.removeEventListener('storage_update', n);
};

export const createTournament = async (t: any) => {
    const db = getLS();
    const newT = { id: Math.random().toString(36).substr(2, 9), ...t, teams: [], matches: [], status: 'ACTIVE', createdAt: new Date().toISOString() };
    db.push(newT);
    saveLS(db);
    return newT.id;
};

export const registerTeam = async (tId: string, team: any) => {
    const db = getLS();
    const t = db.find(x => x.id === tId);
    if (!t) return;
    t.teams.push({ ...team, id: Math.random().toString(36).substr(2, 9), status: RegistrationStatus.PENDING, registeredAt: new Date().toISOString() });
    saveLS(db);
};

export const updateTeamStatus = async (tId: string, teamId: string, status: string) => {
    const db = getLS();
    const t = db.find(x => x.id === tId);
    if (!t) return;
    t.teams = t.teams.map(tm => tm.id === teamId ? { ...tm, status: status as RegistrationStatus } : tm);
    saveLS(db);
};

export const assignTeamGroup = async (tId: string, teamId: string, groupId: string) => {
    const db = getLS();
    const t = db.find(x => x.id === tId);
    if (!t) return;
    t.teams = t.teams.map(tm => tm.id === teamId ? { ...tm, groupId } : tm);
    saveLS(db);
};

export const generateSchedule = async (tId: string, knockoutConfig?: any[]) => {
    const db = getLS();
    const t = db.find(x => x.id === tId);
    if (!t) return;
    t.matches = calculateSchedule(t, knockoutConfig);
    saveLS(db);
};

export const updateMatchDetails = async (tId: string, mId: string, updates: Partial<Match>) => {
    const db = getLS();
    const t = db.find(x => x.id === tId);
    if (!t) return;
    t.matches = t.matches.map(m => m.id === mId ? { ...m, ...updates } : m);
    saveLS(db);
};

export const updateMatchScore = async (tId: string, mId: string, newScore: ScoreState, status: MatchStatus, winnerId?: string) => {
    const db = getLS();
    const t = db.find(x => x.id === tId);
    if (!t) return;

    // 1. Update Score
    const match = t.matches.find(m => m.id === mId);
    if (!match) return;
    match.score = newScore;
    match.status = status;
    match.winnerTeamId = winnerId;

    // 2. Advance Bracket if completed
    if (status === MatchStatus.COMPLETED && winnerId) {
        t.matches = advanceBracket(t.matches, match, winnerId);
    }

    // 3. Recalc Stats
    t.teams = calculateStats(t.teams, t.matches, t.format);

    saveLS(db);
};

export const addRefereeTag = async (tId: string, mId: string, tag: string) => {
    const db = getLS();
    const t = db.find(x => x.id === tId);
    if (!t) return;
    const m = t.matches.find(x => x.id === mId);
    if (m) m.refereeNotes = [...(m.refereeNotes || []), tag];
    saveLS(db);
};

