const genId = () => Math.random().toString(36).substr(2, 9);

const createMatchObject = (tId: string, name: string, round: number, stage: 'GROUP'|'BRACKET'|'PLAYOFF', timeOffset: number, court: string, t1?: string, t2?: string): Match => ({
    id: genId(),
