export const subscribeToTournaments = (cb: (data: Tournament[]) => void) => {
    const q = query(collection(db, TOURNAMENTS_COL), orderBy('createdAt', 'desc'));
    return onSnapshot(q, (snapshot) => {
        const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as Tournament));
        cb(data);
    });
};

export const subscribeToTournament = (id: string, cb: (data: Tournament | null) => void) => {
    return onSnapshot(doc(db, TOURNAMENTS_COL, id), (snapshot) => {
        if (snapshot.exists()) cb({ id: snapshot.id, ...snapshot.data() } as Tournament);
        else cb(null);
    });
};

export const createTournament = async (t: any) => {
    const docRef = await addDoc(collection(db, TOURNAMENTS_COL), { 
        ...t, 
        teams: [], 
        matches: [], 
        status: 'ACTIVE', 
        createdAt: new Date().toISOString() 
    });
    return docRef.id;
};

export const registerTeam = async (tId: string, team: any) => {
    const tournamentRef = doc(db, TOURNAMENTS_COL, tId);
    const snap = await getDoc(tournamentRef);
    if (!snap.exists()) return;
    const data = snap.data() as Tournament;
    const newTeam = { 
        ...team, 
        id: genId(), 
        status: RegistrationStatus.PENDING, 
        registeredAt: new Date().toISOString() 
    };
    await updateDoc(tournamentRef, {
        teams: [...data.teams, newTeam]
    });
};

export const updateTeamStatus = async (tId: string, teamId: string, status: string) => {
    const tournamentRef = doc(db, TOURNAMENTS_COL, tId);
    const snap = await getDoc(tournamentRef);
    if (!snap.exists()) return;
    const data = snap.data() as Tournament;
    const updatedTeams = data.teams.map(tm => tm.id === teamId ? { ...tm, status: status as RegistrationStatus } : tm);
    await updateDoc(tournamentRef, { teams: updatedTeams });
};

export const assignTeamGroup = async (tId: string, teamId: string, groupId: string) => {
    const tournamentRef = doc(db, TOURNAMENTS_COL, tId);
    const snap = await getDoc(tournamentRef);
    if (!snap.exists()) return;
    const data = snap.data() as Tournament;
    const updatedTeams = data.teams.map(tm => tm.id === teamId ? { ...tm, groupId } : tm);
    await updateDoc(tournamentRef, { teams: updatedTeams });
};

export const generateSchedule = async (tId: string, knockoutConfig?: any[]) => {
    const tournamentRef = doc(db, TOURNAMENTS_COL, tId);
    const snap = await getDoc(tournamentRef);
    if (!snap.exists()) return;
    const t = { id: snap.id, ...snap.data() } as Tournament;
    const matches = calculateSchedule(t, knockoutConfig);
    await updateDoc(tournamentRef, { matches });
};

export const updateMatchDetails = async (tId: string, mId: string, updates: Partial<Match>) => {
    const tournamentRef = doc(db, TOURNAMENTS_COL, tId);
    const snap = await getDoc(tournamentRef);
    if (!snap.exists()) return;
    const data = snap.data() as Tournament;
    const updatedMatches = data.matches.map(m => m.id === mId ? { ...m, ...updates } : m);
    await updateDoc(tournamentRef, { matches: updatedMatches });
};

export const updateMatchScore = async (tId: string, mId: string, newScore: ScoreState, status: MatchStatus, winnerId?: string) => {
    const tournamentRef = doc(db, TOURNAMENTS_COL, tId);
    const snap = await getDoc(tournamentRef);
    if (!snap.exists()) return;
    const t = { id: snap.id, ...snap.data() } as Tournament;

    let updatedMatches = t.matches.map(m => {
        if (m.id === mId) return { ...m, score: newScore, status, winnerTeamId: winnerId };
        return m;
    });

    const match = updatedMatches.find(m => m.id === mId);
    if (status === MatchStatus.COMPLETED && winnerId && match) {
        updatedMatches = advanceBracket(updatedMatches, match, winnerId);
    }

    const updatedTeams = calculateStats(t.teams, updatedMatches, t.format);
    await updateDoc(tournamentRef, { matches: updatedMatches, teams: updatedTeams });
};

export const addRefereeTag = async (tId: string, mId: string, tag: string) => {
    const tournamentRef = doc(db, TOURNAMENTS_COL, tId);
    const snap = await getDoc(tournamentRef);
    if (!snap.exists()) return;
    const data = snap.data() as Tournament;
    const updatedMatches = data.matches.map(m => 
        m.id === mId ? { ...m, refereeNotes: [...(m.refereeNotes || []), tag] } : m
    );
    await updateDoc(tournamentRef, { matches: updatedMatches });
};

